import { useState } from "react";
import { Button } from "@/components/ui/button";

interface FashionShoppingGameProps {
  language: string;
  onComplete: (success: boolean) => void;
}

export default function FashionShoppingGame({ language, onComplete }: FashionShoppingGameProps) {
  const [currentScene, setCurrentScene] = useState(0);
  const [score, setScore] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState<string | null>(null);

  const fashionData = {
    french: {
      character: "Bonjour! Je suis Sophie et j'adore la mode!",
      scenes: [
        {
          dialogue: "J'ai besoin d'une robe pour une fête. Quelle couleur recommandez-vous?",
          options: ["Rouge", "Vert", "Jaune"],
          correct: 0,
          feedback: "Parfait! Le rouge est élégant pour une fête!"
        },
        {
          dialogue: "Cette robe est en quelle matière?",
          options: ["Coton", "Soie", "Laine"],
          correct: 1,
          feedback: "Excellent! La soie est parfaite pour les robes de soirée!"
        },
        {
          dialogue: "J'ai aussi besoin de chaussures. Lesquelles vont avec ma robe?",
          options: ["Baskets", "Talons hauts", "Bottes"],
          correct: 1,
          feedback: "Très bien! Les talons hauts complètent parfaitement une robe de soirée!"
        }
      ],
      thanks: "Merci beaucoup! Maintenant je suis prête pour la fête! 💃"
    },
    italian: {
      character: "Ciao! Sono Sofia e amo la moda!",
      scenes: [
        {
          dialogue: "Ho bisogno di un vestito per una festa. Che colore consigli?",
          options: ["Rosso", "Verde", "Giallo"],
          correct: 0,
          feedback: "Perfetto! Il rosso è elegante per una festa!"
        },
        {
          dialogue: "Questo vestito è di che materiale?",
          options: ["Cotone", "Seta", "Lana"],
          correct: 1,
          feedback: "Eccellente! La seta è perfetta per i vestiti da sera!"
        },
        {
          dialogue: "Ho anche bisogno di scarpe. Quali vanno con il mio vestito?",
          options: ["Sneakers", "Tacchi alti", "Stivali"],
          correct: 1,
          feedback: "Molto bene! I tacchi alti completano perfettamente un vestito da sera!"
        }
      ],
      thanks: "Grazie mille! Ora sono pronta per la festa! 💃"
    },
    english: {
      character: "Hello! I'm Sophie and I love fashion!",
      scenes: [
        {
          dialogue: "I need a dress for a party. What color do you recommend?",
          options: ["Red", "Green", "Yellow"],
          correct: 0,
          feedback: "Perfect! Red is elegant for a party!"
        },
        {
          dialogue: "What material is this dress made of?",
          options: ["Cotton", "Silk", "Wool"],
          correct: 1,
          feedback: "Excellent! Silk is perfect for evening dresses!"
        },
        {
          dialogue: "I also need shoes. Which ones go with my dress?",
          options: ["Sneakers", "High heels", "Boots"],
          correct: 1,
          feedback: "Very good! High heels perfectly complement an evening dress!"
        }
      ],
      thanks: "Thank you so much! Now I'm ready for the party! 💃"
    }
  };

  const currentData = fashionData[language as keyof typeof fashionData] || fashionData.english;
  const currentSceneData = currentData.scenes[currentScene];

  const handleAnswer = (answerIndex: number) => {
    setSelectedAnswer(currentSceneData.options[answerIndex]);

    setTimeout(() => {
      if (answerIndex === currentSceneData.correct) {
        setScore(score + 1);
      }

      if (currentScene < currentData.scenes.length - 1) {
        setCurrentScene(currentScene + 1);
        setSelectedAnswer(null);
      } else {
        const success = score >= 2; // Al menos 2 de 3 correctas
        onComplete(success);
      }
    }, 2000);
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-pink-300 to-purple-400 flex items-center justify-center p-6">
      <div className="max-w-4xl w-full grid grid-cols-1 lg:grid-cols-2 gap-8">
        {/* Personaje */}
        <div className="flex flex-col items-center">
          <div className="bg-white rounded-full w-48 h-48 flex items-center justify-center text-8xl mb-4 shadow-2xl">
            👩‍🦱
          </div>
          <div className="bg-white rounded-2xl p-6 shadow-2xl text-center">
            <h3 className="font-bold text-lg mb-2">Sophie</h3>
            <p className="text-gray-600 text-sm">{currentData.character}</p>
          </div>
        </div>

        {/* Diálogo y opciones */}
        <div className="bg-white rounded-2xl p-8 shadow-2xl">
          {currentScene < currentData.scenes.length ? (
            <>
              <div className="bg-pink-100 rounded-lg p-4 mb-6">
                <p className="text-lg text-gray-800">{currentSceneData.dialogue}</p>
              </div>

              {!selectedAnswer ? (
                <div className="space-y-3">
                  {currentSceneData.options.map((option, index) => (
                    <Button
                      key={index}
                      onClick={() => handleAnswer(index)}
                      className="w-full bg-pink-500 hover:bg-pink-600 text-white py-3 text-lg"
                    >
                      {option}
                    </Button>
                  ))}
                </div>
              ) : (
                <div className="text-center">
                  <div className="text-4xl mb-4">
                    {selectedAnswer === currentSceneData.options[currentSceneData.correct] ? "✅" : "❌"}
                  </div>
                  <p className="text-lg font-semibold">
                    {selectedAnswer === currentSceneData.options[currentSceneData.correct] 
                      ? currentSceneData.feedback 
                      : `Respuesta correcta: ${currentSceneData.options[currentSceneData.correct]}`
                    }
                  </p>
                </div>
              )}

              <div className="mt-6 text-center text-sm text-gray-600">
                Escena {currentScene + 1} de {currentData.scenes.length} | Puntuación: {score}
              </div>
            </>
          ) : (
            <div className="text-center">
              <div className="text-6xl mb-4">🎉</div>
              <h2 className="text-2xl font-bold mb-4 text-pink-600">{currentData.thanks}</h2>
              <p className="text-lg">Puntuación final: {score}/{currentData.scenes.length}</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

src/components/games/FootballGame.tsx.content:
import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { gameData } from "@/data/games";
import { Clock, Heart } from "lucide-react";
import GameScoreTracker from "@/components/GameScoreTracker";

interface FootballGameProps {
  language: string;
  onComplete: (success: boolean) => void;
}

export default function FootballGame({ language, onComplete }: FootballGameProps) {
  const [selectedBall, setSelectedBall] = useState<number | null>(null);
  const [showResult, setShowResult] = useState(false);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [score, setScore] = useState(0);
  const [attempts, setAttempts] = useState(3);
  const [timeLeft, setTimeLeft] = useState(120); // 2 minutos
  const [gameStarted, setGameStarted] = useState(false);
  const [gameEnded, setGameEnded] = useState(false);
  const [isGameActive, setIsGameActive] = useState(false);
  const [ballPositions, setBallPositions] = useState([
    { x: 20, y: 60 },
    { x: 50, y: 70 },
    { x: 80, y: 65 }
  ]);

  const questions = gameData.football[language as keyof typeof gameData.football];
  const currentQuestion = questions[currentQuestionIndex];

  // Temporizador
  useEffect(() => {
    if (!gameStarted || gameEnded || timeLeft <= 0) return;

    const timer = setInterval(() => {
      setTimeLeft(prev => {
        if (prev <= 1) {
          setGameEnded(true);
          setIsGameActive(false);
          onComplete(false);
          return 0;
        }
        return prev - 1;
      });
    }, 1000);

    return () => clearInterval(timer);
  }, [gameStarted, gameEnded, timeLeft, onComplete]);

  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  const handleBallClick = (ballIndex: number) => {
    if (showResult || gameEnded) return;
    setSelectedBall(ballIndex);
  };

  const shootBall = () => {
    if (selectedBall === null || gameEnded) return;

    setShowResult(true);
    const isCorrect = selectedBall === currentQuestion.correct;

    if (isCorrect) {
      // Add points for correct answer
      const pointsEarned = 100 + (timeLeft * 2); // Base points + time bonus
      setScore(prev => prev + pointsEarned);

      // Update global score tracker
      if (window.updateGameScore) {
        window.updateGameScore(pointsEarned);
      }

      setTimeout(() => {
        if (currentQuestionIndex < questions.length - 1) {
          // Siguiente pregunta
          setCurrentQuestionIndex(prev => prev + 1);
          setSelectedBall(null);
          setShowResult(false);
          // Nuevas posiciones para las pelotas
          setBallPositions([
            { x: Math.random() * 20 + 10, y: Math.random() * 10 + 60 },
            { x: Math.random() * 20 + 40, y: Math.random() * 10 + 65 },
            { x: Math.random() * 20 + 70, y: Math.random() * 10 + 60 }
          ]);
        } else {
          // Juego completado exitosamente
          setGameEnded(true);
          setIsGameActive(false);
          onComplete(true);
        }
      }, 2000);
    } else {
      setAttempts(prev => {
        const newAttempts = prev - 1;
        if (newAttempts <= 0) {
          setTimeout(() => {
            setGameEnded(true);
            setIsGameActive(false);
            onComplete(false);
          }, 2000);
        } else {
          setTimeout(() => {
            setSelectedBall(null);
            setShowResult(false);
          }, 2000);
        }
        return newAttempts;
      });
    }
  };

  const startGame = () => {
    setGameStarted(true);
    setIsGameActive(true);
    setScore(0);
    setCurrentQuestionIndex(0);
    setAttempts(3);
    setTimeLeft(120);
    setGameEnded(false);
  };

  // Pantalla inicial
  if (!gameStarted) {
    return (
      <div className="min-h-screen bg-gradient-to-b from-green-400 to-green-600 flex items-center justify-center">
        <div className="bg-white/90 backdrop-blur-sm rounded-2xl p-8 text-center max-w-md shadow-2xl">
          <div className="text-6xl mb-6">⚽</div>
          <h2 className="text-2xl font-bold mb-4 text-gray-800">Fútbol de Palabras</h2>
          <p className="text-gray-600 mb-6">
            Tienes 2 minutos y 3 intentos para completar {questions.length} frases correctamente.
            ¡Dispara la pelota con la respuesta correcta al arco!
          </p>
          <Button 
            onClick={startGame} 
            className="bg-green-600 hover:bg-green-700 text-white px-8 py-3 text-lg"
          >
            ¡Comenzar Juego!
          </Button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-b from-green-400 to-green-600 relative overflow-hidden">
      {/* HUD - Temporizador, Intentos, Score */}
      <div className="absolute top-4 left-4 right-4 flex justify-between items-center z-20">
        <div className="bg-white/90 rounded-lg px-4 py-2 flex items-center gap-2">
          <Clock className="w-5 h-5 text-blue-600" />
          <span className={`font-bold ${timeLeft <= 30 ? 'text-red-600 animate-pulse' : 'text-gray-800'}`}>
            {formatTime(timeLeft)}
          </span>
        </div>

        <div className="bg-white/90 rounded-lg px-4 py-2 flex items-center gap-2">
          <span className="text-gray-800 font-semibold">Pregunta {currentQuestionIndex + 1}/{questions.length}</span>
        </div>

        <div className="bg-white/90 rounded-lg px-4 py-2 flex items-center gap-2">
          <span className="text-orange-600 font-bold">🏆 {score}</span>
        </div>

        <div className="bg-white/90 rounded-lg px-4 py-2 flex items-center gap-2">
          <Heart className="w-5 h-5 text-red-500" />
          <span className="font-bold text-gray-800">{attempts}</span>
        </div>
      </div>

      {/* Campo de fútbol */}
      <div className="absolute inset-0">
        <div className="absolute bottom-0 w-full h-32 bg-green-500"></div>
        <div className="absolute bottom-0 left-1/2 transform -translate-x-1/2 w-48 h-24 border-4 border-white rounded-t-3xl"></div>
      </div>

      {/* Arco */}
      <div className="absolute top-20 left-1/2 transform -translate-x-1/2 w-32 h-20 border-4 border-white border-b-0">
        <div className="absolute -top-2 -left-2 -right-2 h-2 bg-white"></div>
        {showResult && selectedBall === currentQuestion.correct && (
          <div className="absolute inset-0 flex items-center justify-center">
            <div className="text-6xl animate-bounce">🎉</div>
          </div>
        )}
      </div>

      {/* Pregunta */}
      <div className="absolute top-44 left-1/2 transform -translate-x-1/2 bg-white/90 rounded-lg p-4 text-black text-center max-w-md">
        <h3 className="text-lg font-bold mb-2">Completa la frase:</h3>
        <p className="text-xl mb-2">{currentQuestion.question}</p>
        {showResult && (
          <p className="text-sm text-green-600 font-medium">
            {currentQuestion.explanation}
          </p>
        )}
      </div>

      {/* Pelotas */}
      {currentQuestion.options.map((option, index) => (
        <div
          key={index}
          className={`absolute cursor-pointer transition-all duration-300 ${
            selectedBall === index ? 'scale-110 ring-4 ring-yellow-400' : 'hover:scale-105'
          }`}
          style={{
            left: `${ballPositions[index].x}%`,
            top: `${ballPositions[index].y}%`,
            transform: showResult && selectedBall === index ? 'translateY(-300px)' : 'none'
          }}
          onClick={() => handleBallClick(index)}
        >
          <div className="relative">
            <div className="w-16 h-16 bg-white rounded-full shadow-lg flex items-center justify-center border-2 border-gray-300">
              <span className="text-sm font-bold text-gray-800">{option}</span>
            </div>
          </div>
        </div>
      ))}

      {/* Botón disparar */}
      <div className="absolute bottom-20 left-1/2 transform -translate-x-1/2">
        <Button
          onClick={shootBall}
          disabled={selectedBall === null || showResult || gameEnded}
          className="bg-yellow-500 hover:bg-yellow-600 text-black font-bold px-8 py-3 text-lg shadow-lg disabled:opacity-50"
        >
          ¡Disparar! ⚽
        </Button>
      </div>

      {/* Mensaje de resultado */}
      {showResult && (
        <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 bg-white/95 rounded-xl p-6 text-center shadow-2xl z-30">
          <div className="text-4xl mb-2">
            {selectedBall === currentQuestion.correct ? '🎉' : '❌'}
          </div>
          <h3 className="text-xl font-bold mb-2 text-gray-800">
            {selectedBall === currentQuestion.correct ? '¡Correcto!' : '¡Incorrecto!'}
          </h3>
          <p className="text-gray-600">
            {selectedBall === currentQuestion.correct 
              ? '¡Buen disparo! Siguiente pregunta...' 
              : attempts > 1 ? 'Inténtalo de nuevo...' : '¡Se acabaron los intentos!'
            }
          </p>
        </div>
      )}

      {/* Game Score Tracker */}
      {gameStarted && (
        <div className="absolute top-4 left-1/2 transform -translate-x-1/2 z-30">
          <GameScoreTracker
            gameId="football-words"
            gameName="Fútbol de Palabras"
            difficulty="Intermedio"
            language={language}
            isGameActive={isGameActive}
            onGameStart={() => {}}
            onGameEnd={(finalScore, completionTime) => {
              console.log(`Game completed with score: ${finalScore}, time: ${completionTime}s`);
            }}
          />
        </div>
      )}
    </div>
  );
}

src/components/games/FootballWordsGame.t.content:
import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import GameScoreTracker from "@/components/GameScoreTracker";
import { useGameScore } from "@/hooks/useGameScore";
import { Trophy } from "lucide-react";

interface FootballWordsGameProps {
  language: string;
  difficulty: "Principiante" | "Intermedio" | "Avanzado";
}

const vocabularyData = {
  spanish: {
    Principiante: [
      { word: "balón", translation: "ball", points: 10 },
      { word: "gol", translation: "goal", points: 10 },
      { word: "portería", translation: "goalpost", points: 15 },
      { word: "jugador", translation: "player", points: 10 },
      { word: "árbitro", translation: "referee", points: 15 },
    ],
    Intermedio: [
      { word: "lateral", translation: "sideline", points: 20 },
      { word: "saque", translation: "throw-in", points: 20 },
      { word: "penal", translation: "penalty", points: 25 },
      { word: "córner", translation: "corner", points: 20 },
      { word: "defensa", translation: "defense", points: 20 },
    ],
    Avanzado: [
      { word: "contraataque", translation: "counterattack", points: 30 },
      { word: "estrategia", translation: "strategy", points: 30 },
      { word: "formación", translation: "formation", points: 35 },
      { word: "fuera de juego", translation: "offside", points: 40 },
      { word: "prórroga", translation: "overtime", points: 35 },
    ]
  },
  english: {
    Principiante: [
      { word: "football", translation: "fútbol", points: 10 },
      { word: "team", translation: "equipo", points: 10 },
      { word: "match", translation: "partido", points: 15 },
      { word: "field", translation: "campo", points: 10 },
      { word: "coach", translation: "entrenador", points: 15 },
    ],
    Intermedio: [
      { word: "striker", translation: "delantero", points: 20 },
      { word: "midfielder", translation: "centrocampista", points: 25 },
      { word: "goalkeeper", translation: "portero", points: 20 },
      { word: "substitute", translation: "suplente", points: 25 },
      { word: "tournament", translation: "torneo", points: 20 },
    ],
    Avanzado: [
      { word: "championship", translation: "campeonato", points: 30 },
      { word: "tactics", translation: "táctica", points: 30 },
      { word: "possession", translation: "posesión", points: 35 },
      { word: "relegation", translation: "descenso", points: 40 },
      { word: "promotion", translation: "ascenso", points: 35 },
    ]
  }
};

export default function FootballWordsGame({ language, difficulty }: FootballWordsGameProps) {
  const { isGameActive, startGame, endGame, addPoints } = useGameScore();
  const [currentWordIndex, setCurrentWordIndex] = useState(0);
  const [userAnswer, setUserAnswer] = useState("");
  const [feedback, setFeedback] = useState("");
  const [gameWords, setGameWords] = useState<Array<{ word: string; translation: string; points: number }>>([]);
  const [correctAnswers, setCorrectAnswers] = useState(0);
  const [totalWords, setTotalWords] = useState(0);

  useEffect(() => {
    const words = vocabularyData[language as keyof typeof vocabularyData]?.[difficulty] || vocabularyData.spanish[difficulty];
    setGameWords([...words].sort(() => Math.random() - 0.5));
    setTotalWords(words.length);
  }, [language, difficulty]);

  const handleStartGame = () => {
    startGame();
    setCurrentWordIndex(0);
    setUserAnswer("");
    setFeedback("");
    setCorrectAnswers(0);
  };

  const handleSubmitAnswer = () => {
    if (!userAnswer.trim() || !isGameActive) return;

    const currentWord = gameWords[currentWordIndex];
    const isCorrect = userAnswer.toLowerCase().trim() === currentWord.translation.toLowerCase();

    if (isCorrect) {
      addPoints(currentWord.points);
      setCorrectAnswers(prev => prev + 1);
      setFeedback(`¡Correcto! +${currentWord.points} puntos`);
    } else {
      setFeedback(`Incorrecto. La respuesta correcta era: ${currentWord.translation}`);
    }

    setTimeout(() => {
      if (currentWordIndex + 1 >= gameWords.length) {
        endGame();
        setFeedback(`¡Juego completado! ${correctAnswers + (isCorrect ? 1 : 0)}/${totalWords} respuestas correctas`);
      } else {
        setCurrentWordIndex(prev => prev + 1);
        setUserAnswer("");
        setFeedback("");
      }
    }, 2000);
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === "Enter") {
      handleSubmitAnswer();
    }
  };

  return (
    <div className="space-y-6">
      <GameScoreTracker
        gameId="football-words"
        gameName="Palabras de Fútbol"
        difficulty={difficulty}
        language={language}
        isGameActive={isGameActive}
        onGameStart={() => console.log("Game started")}
        onGameEnd={(finalScore, time) => console.log(`Game ended: ${finalScore} points in ${time}s`)}
      />

      <Card className="bg-white/10 border-white/20">
        <CardHeader>
          <CardTitle className="text-white flex items-center gap-2">
            ⚽ Palabras de Fútbol
            <div className="ml-auto text-sm">
              {isGameActive && (
                <span className="text-orange-400">
                  {currentWordIndex + 1}/{gameWords.length}
                </span>
              )}
            </div>
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-6">
          {!isGameActive ? (
            <div className="text-center space-y-4">
              <div className="text-lg text-blue-200">
                Traduce las palabras de fútbol al {language === "spanish" ? "español" : "inglés"}
              </div>
              <div className="text-sm text-blue-300">
                Dificultad: {difficulty} | {gameWords.length} palabras
              </div>
              <Button
                onClick={handleStartGame}
                className="bg-orange-500 hover:bg-orange-600 text-white"
                size="lg"
              >
                <Trophy className="h-5 w-5 mr-2" />
                Comenzar Juego
              </Button>
            </div>
          ) : (
            <div className="space-y-6">
              <div className="text-center">
                <div className="text-3xl font-bold text-orange-400 mb-2">
                  {gameWords[currentWordIndex]?.word}
                </div>
                <div className="text-blue-200">
                  Traduce esta palabra ({gameWords[currentWordIndex]?.points} puntos)
                </div>
              </div>

              <div className="space-y-4">
                <Input
                  value={userAnswer}
                  onChange={(e) => setUserAnswer(e.target.value)}
                  onKeyPress={handleKeyPress}
                  placeholder="Escribe tu respuesta..."
                  className="bg-white/10 border-white/30 text-white placeholder:text-blue-300"
                  disabled={!!feedback}
                />

                <Button
                  onClick={handleSubmitAnswer}
                  disabled={!userAnswer.trim() || !!feedback}
                  className="w-full bg-blue-500 hover:bg-blue-600 text-white"
                >
                  Enviar Respuesta
                </Button>
              </div>

              {feedback && (
                <div className={`text-center p-4 rounded-lg ${
                  feedback.includes("Correcto") 
                    ? "bg-green-500/20 text-green-300 border border-green-500/30" 
                    : "bg-red-500/20 text-red-300 border border-red-500/30"
                }`}>
                  {feedback}
                </div>
              )}

              <div className="text-center text-sm text-blue-300">
                Presiona Enter para enviar tu respuesta
              </div>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

src/components/games/GameModal.tsx.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { X } from "lucide-react";
import FootballGame from "./FootballGame";
import VacationRoutineGame from "./VacationRoutineGame";
import ImageAssociationGame from "./ImageAssociationGame";
import SnoopyPathGame from "./SnoopyPathGame";
import FashionShoppingGame from "./FashionShoppingGame";
import MariaShoppingGame from "./MariaShoppingGame";
import SpidermanTrainGame from "./SpidermanTrainGame";
import TreasureHuntGame from "./TreasureHuntGame";
import WordSoupGame from "./WordSoupGame";
import PlantCareGame from "./PlantCareGame";

interface GameModalProps {
  gameId: string;
  gameName: string;
  isOpen: boolean;
  onClose: () => void;
}

export default function GameModal({ gameId, gameName, isOpen, onClose }: GameModalProps) {
  const [selectedLanguage, setSelectedLanguage] = useState<string | null>(null);
  const [gameCompleted, setGameCompleted] = useState(false);
  const [gameResult, setGameResult] = useState<boolean | null>(null);

  const languages = [
    { id: "french", name: "Francés", flag: "🇫🇷" },
    { id: "italian", name: "Italiano", flag: "🇮🇹" },
    { id: "portuguese", name: "Portugués", flag: "🇵🇹" },
    { id: "english", name: "Inglés", flag: "🇺🇸" },
    { id: "russian", name: "Ruso", flag: "🇷🇺" }
  ];

  const handleGameComplete = (success: boolean) => {
    setGameResult(success);
    setGameCompleted(true);

    setTimeout(() => {
      onClose();
      setSelectedLanguage(null);
      setGameCompleted(false);
      setGameResult(null);
    }, 3000);
  };

  const renderGame = () => {
    if (!selectedLanguage) return null;

    const gameProps = {
      language: selectedLanguage,
      onComplete: handleGameComplete
    };

    switch (gameId) {
      case "football-words":
        return <FootballGame {...gameProps} />;
      case "vacation-routine":
        return <VacationRoutineGame {...gameProps} />;
      case "image-association":
        return <ImageAssociationGame {...gameProps} />;
      case "snoopy-path":
        return <SnoopyPathGame {...gameProps} />;
      case "fashion-shopping":
        return <FashionShoppingGame {...gameProps} />;
      case "maria-shopping":
        return <MariaShoppingGame {...gameProps} />;
      case "spiderman-train":
        return <SpidermanTrainGame {...gameProps} />;
      case "treasure-hunt":
        return <TreasureHuntGame {...gameProps} />;
      case "word-soup":
        return <WordSoupGame {...gameProps} />;
      case "plant-care":
        return <PlantCareGame {...gameProps} />;
      default:
        return (
          <div className="min-h-screen bg-gradient-to-b from-blue-400 to-purple-500 flex items-center justify-center">
            <div className="bg-white rounded-2xl p-8 text-center">
              <h2 className="text-2xl font-bold mb-4">Juego en Desarrollo</h2>
              <p className="text-gray-600 mb-4">Este juego estará disponible pronto!</p>
              <Button onClick={onClose} className="bg-blue-600 hover:bg-blue-700">
                Volver
              </Button>
            </div>
          </div>
        );
    }
  };

  if (!isOpen) return null;

  if (gameCompleted && gameResult !== null) {
    return (
      <div className="fixed inset-0 z-50 bg-black/80 backdrop-blur-sm flex items-center justify-center">
        <div className={`${gameResult ? 'bg-gradient-to-br from-green-400 to-emerald-500' : 'bg-gradient-to-br from-orange-400 to-red-500'} rounded-2xl p-8 text-center max-w-md mx-4 shadow-2xl border border-white/20`}>
          {/* Confetti effect for success */}
          {gameResult && (
            <div className="absolute inset-0 pointer-events-none">
              <div className="absolute top-2 left-8 w-3 h-3 bg-yellow-300 rounded-full animate-bounce"></div>
              <div className="absolute top-4 right-6 w-2 h-2 bg-pink-300 rounded-full animate-pulse"></div>
              <div className="absolute bottom-6 left-4 w-2 h-2 bg-blue-300 rounded-full animate-bounce" style={{animationDelay: '0.5s'}}></div>
              <div className="absolute bottom-4 right-8 w-3 h-3 bg-purple-300 rounded-full animate-pulse" style={{animationDelay: '1s'}}></div>
            </div>
          )}

          <div className="relative z-10">
            <div className="text-8xl mb-6 animate-bounce">
              {gameResult ? "🎉" : "💪"}
            </div>
            <h2 className="text-3xl font-bold mb-4 text-white drop-shadow-lg">
              {gameResult ? "¡Felicitaciones!" : "¡Sigue intentando!"}
            </h2>
            <p className="text-white/90 text-lg">
              {gameResult 
                ? "¡Has completado el juego exitosamente!" 
                : "No te rindas, ¡inténtalo de nuevo!"
              }
            </p>
          </div>
        </div>
      </div>
    );
  }

  if (selectedLanguage) {
    return (
      <div className="fixed inset-0 z-50">
        <Button
          onClick={onClose}
          className="absolute top-4 right-4 z-50 bg-red-600 hover:bg-red-700 rounded-full w-12 h-12 p-0"
        >
          <X size={24} />
        </Button>
        {renderGame()}
      </div>
    );
  }

  return (
    <div className="fixed inset-0 z-50 bg-black/80 backdrop-blur-sm flex items-center justify-center p-6">
      <div className="bg-gradient-to-br from-blue-900/90 to-purple-900/90 backdrop-blur-md rounded-2xl p-8 max-w-2xl w-full border border-white/20 shadow-2xl">
        {/* Decorative background */}
        <div className="absolute inset-0 opacity-10 pointer-events-none">
          <div className="absolute top-4 right-8 w-16 h-16 bg-orange-400 rounded-full blur-xl"></div>
          <div className="absolute bottom-8 left-6 w-12 h-12 bg-yellow-400 rounded-full blur-xl"></div>
        </div>

        <div className="relative z-10">
          <div className="flex justify-between items-center mb-6">
            <h2 className="text-2xl font-bold text-white drop-shadow-lg">{gameName}</h2>
            <Button 
              onClick={onClose} 
              variant="ghost" 
              size="icon"
              className="text-white hover:bg-white/10"
            >
              <X size={20} />
            </Button>
          </div>

          <div className="mb-6">
            <h3 className="text-lg font-semibold mb-6 text-white/90">Elige tu idioma:</h3>
            <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
              {languages.map((lang, index) => {
                const colors = [
                  "bg-gradient-to-r from-red-500 to-red-600 hover:from-red-600 hover:to-red-700",
                  "bg-gradient-to-r from-green-500 to-green-600 hover:from-green-600 hover:to-green-700",
                  "bg-gradient-to-r from-blue-500 to-blue-600 hover:from-blue-600 hover:to-blue-700",
                  "bg-gradient-to-r from-purple-500 to-purple-600 hover:from-purple-600 hover:to-purple-700",
                  "bg-gradient-to-r from-orange-500 to-orange-600 hover:from-orange-600 hover:to-orange-700"
                ];

                return (
                  <Button
                    key={lang.id}
                    onClick={() => setSelectedLanguage(lang.id)}
                    className={`flex items-center justify-center gap-3 h-16 ${colors[index]} text-white font-semibold shadow-lg transform hover:scale-105 transition-all duration-200 border border-white/20`}
                  >
                    <span className="text-2xl">{lang.flag}</span>
                    <span className="text-lg">{lang.name}</span>
                  </Button>
                );
              })}
            </div>
          </div>

          <div className="text-center text-white/70">
            <p>Selecciona un idioma para comenzar el juego</p>
          </div>
        </div>
      </div>
    </div>
  );
}

src/components/games/ImageAssociationGam.content:
import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { gameData } from "@/data/games";

interface ImageAssociationGameProps {
  language: string;
  onComplete: (success: boolean) => void;
}

export default function ImageAssociationGame({ language, onComplete }: ImageAssociationGameProps) {
  const [matches, setMatches] = useState<{[key: string]: boolean}>({});
  const [selectedWord, setSelectedWord] = useState<string | null>(null);
  const [selectedImage, setSelectedImage] = useState<string | null>(null);
  const [timeLeft, setTimeLeft] = useState(60);
  const [gameStarted, setGameStarted] = useState(false);

  const gameInfo = gameData.association[language as keyof typeof gameData.association];

  // Datos por defecto si el idioma no está configurado
  const defaultItems = [
    { word: "Cat", image: "🐱", category: "animal" },
    { word: "Red", image: "🔴", category: "color" },
    { word: "Cotton", image: "🧵", category: "fabric" },
    { word: "Hand", image: "✋", category: "body" }
  ];

  const items = gameInfo ? gameInfo.items : defaultItems;

  useEffect(() => {
    if (gameStarted && timeLeft > 0) {
      const timer = setTimeout(() => setTimeLeft(timeLeft - 1), 1000);
      return () => clearTimeout(timer);
    } else if (timeLeft === 0) {
      onComplete(false);
    }
  }, [gameStarted, timeLeft, onComplete]);

  useEffect(() => {
    const matchedCount = Object.values(matches).filter(Boolean).length;
    if (matchedCount === items.length) {
      onComplete(true);
    }
  }, [matches, items.length, onComplete]);

  const handleWordClick = (word: string) => {
    if (matches[word]) return;
    setSelectedWord(selectedWord === word ? null : word);
  };

  const handleImageClick = (word: string) => {
    if (matches[word]) return;
    setSelectedImage(selectedImage === word ? null : word);
  };

  const checkMatch = () => {
    if (selectedWord && selectedImage && selectedWord === selectedImage) {
      setMatches({...matches, [selectedWord]: true});
      setSelectedWord(null);
      setSelectedImage(null);
    } else {
      setSelectedWord(null);
      setSelectedImage(null);
    }
  };

  useEffect(() => {
    if (selectedWord && selectedImage) {
      setTimeout(checkMatch, 500);
    }
  }, [selectedWord, selectedImage]);

  if (!gameStarted) {
    return (
      <div className="min-h-screen bg-gradient-to-b from-purple-400 to-pink-500 flex items-center justify-center">
        <div className="bg-white rounded-2xl p-8 text-center shadow-2xl">
          <h2 className="text-3xl font-bold mb-4">Asociación de Imágenes</h2>
          <p className="text-gray-600 mb-6">Conecta cada palabra con su imagen correspondiente</p>
          <Button 
            onClick={() => setGameStarted(true)}
            className="bg-purple-600 hover:bg-purple-700 px-8 py-3 text-lg"
          >
            ¡Empezar Juego!
          </Button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-b from-purple-400 to-pink-500 p-6">
      <div className="max-w-4xl mx-auto">
        {/* Timer */}
        <div className="text-center mb-6">
          <div className="bg-white rounded-lg p-4 inline-block shadow-lg">
            <div className="text-2xl font-bold text-purple-600">
              ⏰ {timeLeft}s
            </div>
          </div>
        </div>

        <div className="grid grid-cols-2 gap-8">
          {/* Palabras */}
          <div className="space-y-4">
            <h3 className="text-2xl font-bold text-white text-center mb-4">Palabras</h3>
            {items.map((item, index) => (
              <Button
                key={`word-${index}`}
                onClick={() => handleWordClick(item.word)}
                disabled={matches[item.word]}
                className={`w-full h-16 text-lg ${
                  matches[item.word] 
                    ? 'bg-green-500 text-white cursor-not-allowed'
                    : selectedWord === item.word
                    ? 'bg-yellow-400 text-black'
                    : 'bg-white text-black hover:bg-gray-100'
                }`}
              >
                {matches[item.word] ? '✅ ' + item.word : item.word}
              </Button>
            ))}
          </div>

          {/* Imágenes */}
          <div className="space-y-4">
            <h3 className="text-2xl font-bold text-white text-center mb-4">Imágenes</h3>
            {items.map((item, index) => (
              <Button
                key={`image-${index}`}
                onClick={() => handleImageClick(item.word)}
                disabled={matches[item.word]}
                className={`w-full h-16 text-4xl ${
                  matches[item.word]
                    ? 'bg-green-500 cursor-not-allowed'
                    : selectedImage === item.word
                    ? 'bg-yellow-400'
                    : 'bg-white hover:bg-gray-100'
                }`}
              >
                {item.image}
              </Button>
            ))}
          </div>
        </div>

        {/* Progreso */}
        <div className="mt-8 text-center">
          <div className="bg-white rounded-lg p-4 inline-block">
            <div className="text-lg font-semibold">
              Progreso: {Object.values(matches).filter(Boolean).length} / {items.length}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

