import { useState } from "react";
import { Button } from "@/components/ui/button";

interface MariaShoppingGameProps {
  language: string;
  onComplete: (success: boolean) => void;
}

export default function MariaShoppingGame({ language, onComplete }: MariaShoppingGameProps) {
  const [currentTask, setCurrentTask] = useState(0);
  const [score, setScore] = useState(0);
  const [total, setTotal] = useState(0);

  const shoppingData = {
    french: {
      character: "Bonjour! Je suis Maria. Pouvez-vous m'aider avec mes courses?",
      tasks: [
        {
          question: "Où puis-je acheter du pain?",
          options: ["Boulangerie", "Pharmacie", "Banque"],
          correct: 0,
          price: 3
        },
        {
          question: "J'ai besoin de lait. Combien ça coûte?",
          options: ["5 euros", "2 euros", "10 euros"],
          correct: 1,
          price: 2
        },
        {
          question: "Pour acheter des médicaments, je vais à...",
          options: ["Boulangerie", "Pharmacie", "Épicerie"],
          correct: 1,
          price: 15
        }
      ],
      thanks: "Merci beaucoup! Vous m'avez aidée avec toutes mes courses!"
    },
    italian: {
      character: "Ciao! Sono Maria. Puoi aiutarmi con la spesa?",
      tasks: [
        {
          question: "Dove posso comprare il pane?",
          options: ["Panetteria", "Farmacia", "Banca"],
          correct: 0,
          price: 3
        },
        {
          question: "Ho bisogno di latte. Quanto costa?",
          options: ["5 euro", "2 euro", "10 euro"],
          correct: 1,
          price: 2
        },
        {
          question: "Per comprare medicine, vado in...",
          options: ["Panetteria", "Farmacia", "Negozio"],
          correct: 1,
          price: 15
        }
      ],
      thanks: "Grazie mille! Mi hai aiutato con tutta la spesa!"
    },
    english: {
      character: "Hello! I'm Maria. Can you help me with my shopping?",
      tasks: [
        {
          question: "Where can I buy bread?",
          options: ["Bakery", "Pharmacy", "Bank"],
          correct: 0,
          price: 3
        },
        {
          question: "I need milk. How much does it cost?",
          options: ["5 dollars", "2 dollars", "10 dollars"],
          correct: 1,
          price: 2
        },
        {
          question: "To buy medicine, I go to...",
          options: ["Bakery", "Pharmacy", "Grocery store"],
          correct: 1,
          price: 15
        }
      ],
      thanks: "Thank you so much! You helped me with all my shopping!"
    }
  };

  const currentData = shoppingData[language as keyof typeof shoppingData] || shoppingData.english;
  const currentTaskData = currentData.tasks[currentTask];

  const handleAnswer = (answerIndex: number) => {
    if (answerIndex === currentTaskData.correct) {
      setScore(score + 1);
      setTotal(total + currentTaskData.price);
    }

    setTimeout(() => {
      if (currentTask < currentData.tasks.length - 1) {
        setCurrentTask(currentTask + 1);
      } else {
        onComplete(score >= 2);
      }
    }, 2000);
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-indigo-400 to-blue-600 flex items-center justify-center p-6">
      <div className="max-w-4xl w-full grid grid-cols-1 lg:grid-cols-2 gap-8">
        {/* Maria */}
        <div className="flex flex-col items-center">
          <div className="bg-white rounded-full w-48 h-48 flex items-center justify-center text-8xl mb-4 shadow-2xl">
            👩‍🍳
          </div>
          <div className="bg-white rounded-2xl p-6 shadow-2xl text-center">
            <h3 className="font-bold text-lg mb-2">Maria</h3>
            <p className="text-gray-600 text-sm">{currentData.character}</p>
            <div className="mt-4 p-3 bg-green-100 rounded-lg">
              <p className="text-sm font-semibold">Total gastado: {total} €</p>
            </div>
          </div>
        </div>

        {/* Shopping task */}
        <div className="bg-white rounded-2xl p-8 shadow-2xl">
          {currentTask < currentData.tasks.length ? (
            <>
              <div className="bg-indigo-100 rounded-lg p-4 mb-6">
                <p className="text-lg text-gray-800">{currentTaskData.question}</p>
                <p className="text-sm text-gray-600 mt-2">Precio: {currentTaskData.price} €</p>
              </div>

              <div className="space-y-3">
                {currentTaskData.options.map((option, index) => (
                  <Button
                    key={index}
                    onClick={() => handleAnswer(index)}
                    className="w-full bg-indigo-500 hover:bg-indigo-600 text-white py-3 text-lg"
                  >
                    {option}
                  </Button>
                ))}
              </div>

              <div className="mt-6 text-center text-sm text-gray-600">
                Tarea {currentTask + 1} de {currentData.tasks.length} | Puntuación: {score}
              </div>
            </>
          ) : (
            <div className="text-center">
              <div className="text-6xl mb-4">🛒</div>
              <h2 className="text-2xl font-bold mb-4 text-indigo-600">{currentData.thanks}</h2>
              <div className="bg-green-100 rounded-lg p-4">
                <p className="text-lg">Puntuación: {score}/{currentData.tasks.length}</p>
                <p className="text-lg">Total gastado: {total} €</p>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

src/components/games/PlantCareGame.tsx.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";

interface PlantCareGameProps {
  language: string;
  onComplete: (success: boolean) => void;
}

export default function PlantCareGame({ language, onComplete }: PlantCareGameProps) {
  const [currentSeason, setCurrentSeason] = useState("spring");
  const [currentWeather, setCurrentWeather] = useState("sunny");
  const [currentTime, setCurrentTime] = useState("morning");
  const [plantHealth, setPlantHealth] = useState(100);
  const [actions, setActions] = useState(0);
  const [maxActions] = useState(5);

  const plantData = {
    french: {
      seasons: {
        spring: "Printemps - Arrosez modérément, la plante pousse",
        summer: "Été - Arrosez beaucoup, protégez du soleil intense",
        autumn: "Automne - Réduisez l'arrosage, préparez pour l'hiver",
        winter: "Hiver - Arrosez très peu, gardez au chaud"
      },
      weather: {
        sunny: "Ensoleillé - Plus d'eau nécessaire",
        rainy: "Pluvieux - Moins d'eau nécessaire",
        cloudy: "Nuageux - Arrosage normal"
      },
      times: {
        morning: "Matin (7h) - Meilleur moment pour arroser",
        afternoon: "Après-midi (14h) - Évitez d'arroser en plein soleil",
        evening: "Soir (19h) - Bon moment pour arroser"
      },
      actions: {
        water: "Arroser",
        fertilize: "Fertiliser",
        shade: "Mettre à l'ombre",
        move_sun: "Mettre au soleil"
      },
      note: "Note de la voisine: 'Arrosez ma marguerite le matin ou le soir, pas trop en hiver!'"
    },
    italian: {
      seasons: {
        spring: "Primavera - Innaffia moderatamente, la pianta cresce",
        summer: "Estate - Innaffia molto, proteggi dal sole intenso",
        autumn: "Autunno - Riduci l'innaffiatura, prepara per l'inverno",
        winter: "Inverno - Innaffia molto poco, tieni al caldo"
      },
      weather: {
        sunny: "Soleggiato - Più acqua necessaria",
        rainy: "Piovoso - Meno acqua necessaria", 
        cloudy: "Nuvoloso - Innaffiatura normale"
      },
      times: {
        morning: "Mattina (7h) - Miglior momento per innaffiare",
        afternoon: "Pomeriggio (14h) - Evita di innaffiare sotto il sole",
        evening: "Sera (19h) - Buon momento per innaffiare"
      },
      actions: {
        water: "Innaffiare",
        fertilize: "Fertilizzare",
        shade: "Mettere all'ombra",
        move_sun: "Mettere al sole"
      },
      note: "Nota della vicina: 'Innaffia la mia margherita la mattina o la sera, non troppo in inverno!'"
    },
    english: {
      seasons: {
        spring: "Spring - Water moderately, plant is growing",
        summer: "Summer - Water a lot, protect from intense sun",
        autumn: "Autumn - Reduce watering, prepare for winter",
        winter: "Winter - Water very little, keep warm"
      },
      weather: {
        sunny: "Sunny - More water needed",
        rainy: "Rainy - Less water needed",
        cloudy: "Cloudy - Normal watering"
      },
      times: {
        morning: "Morning (7am) - Best time to water",
        afternoon: "Afternoon (2pm) - Avoid watering in full sun",
        evening: "Evening (7pm) - Good time to water"
      },
      actions: {
        water: "Water",
        fertilize: "Fertilize",
        shade: "Move to shade",
        move_sun: "Move to sun"
      },
      note: "Neighbor's note: 'Water my daisy in the morning or evening, not too much in winter!'"
    }
  };

  const currentData = plantData[language as keyof typeof plantData] || plantData.english;

  const getOptimalAction = () => {
    if (currentSeason === "winter" && currentWeather !== "sunny") return "fertilize";
    if (currentTime === "afternoon" && currentWeather === "sunny") return "shade";
    if ((currentTime === "morning" || currentTime === "evening") && currentWeather !== "rainy") return "water";
    if (currentWeather === "cloudy" && currentSeason !== "winter") return "water";
    return "move_sun";
  };

  const handleAction = (action: string) => {
    const optimal = getOptimalAction();
    const isCorrect = action === optimal;

    if (isCorrect) {
      setPlantHealth(Math.min(100, plantHealth + 10));
    } else {
      setPlantHealth(Math.max(0, plantHealth - 20));
    }

    setActions(actions + 1);

    // Cambiar condiciones para la siguiente ronda
    setTimeout(() => {
      const seasons = ["spring", "summer", "autumn", "winter"];
      const weathers = ["sunny", "rainy", "cloudy"];
      const times = ["morning", "afternoon", "evening"];

      setCurrentSeason(seasons[Math.floor(Math.random() * seasons.length)]);
      setCurrentWeather(weathers[Math.floor(Math.random() * weathers.length)]);
      setCurrentTime(times[Math.floor(Math.random() * times.length)]);

      if (actions >= maxActions - 1) {
        onComplete(plantHealth > 50);
      }
    }, 2000);
  };

  const getPlantEmoji = () => {
    if (plantHealth > 80) return "🌼";
    if (plantHealth > 60) return "🌸";
    if (plantHealth > 40) return "🌺";
    if (plantHealth > 20) return "🥀";
    return "💀";
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-green-300 to-emerald-500 flex items-center justify-center p-6">
      <div className="max-w-4xl w-full grid grid-cols-1 lg:grid-cols-2 gap-8">
        {/* Planta */}
        <div className="bg-white rounded-2xl p-8 shadow-2xl text-center">
          <h3 className="text-2xl font-bold mb-4">Margarita de la Vecina</h3>

          <div className="text-8xl mb-4">{getPlantEmoji()}</div>

          <div className="bg-green-100 rounded-lg p-4 mb-4">
            <div className="text-lg font-semibold text-green-800">
              Salud: {plantHealth}%
            </div>
            <div className="w-full bg-gray-200 rounded-full h-4 mt-2">
              <div 
                className={`h-4 rounded-full transition-all duration-500 ${
                  plantHealth > 60 ? 'bg-green-500' : plantHealth > 30 ? 'bg-yellow-500' : 'bg-red-500'
                }`}
                style={{ width: `${plantHealth}%` }}
              ></div>
            </div>
          </div>

          <div className="bg-blue-100 rounded-lg p-3 text-sm">
            <p className="text-blue-800 italic">{currentData.note}</p>
          </div>
        </div>

        {/* Panel de control */}
        <div className="bg-white rounded-2xl p-8 shadow-2xl">
          <h3 className="text-xl font-bold mb-4">Condiciones Actuales</h3>

          <div className="space-y-4 mb-6">
            <div className="bg-orange-100 rounded-lg p-3">
              <strong>Estación:</strong> {currentData.seasons[currentSeason as keyof typeof currentData.seasons]}
            </div>
            <div className="bg-blue-100 rounded-lg p-3">
              <strong>Clima:</strong> {currentData.weather[currentWeather as keyof typeof currentData.weather]}
            </div>
            <div className="bg-purple-100 rounded-lg p-3">
              <strong>Hora:</strong> {currentData.times[currentTime as keyof typeof currentData.times]}
            </div>
          </div>

          <h4 className="font-semibold mb-3">¿Qué debes hacer?</h4>
          <div className="grid grid-cols-2 gap-3">
            <Button
              onClick={() => handleAction("water")}
              className="bg-blue-500 hover:bg-blue-600 py-3"
            >
              💧 {currentData.actions.water}
            </Button>
            <Button
              onClick={() => handleAction("fertilize")}
              className="bg-brown-500 hover:bg-brown-600 py-3 bg-amber-600 hover:bg-amber-700"
            >
              🌱 {currentData.actions.fertilize}
            </Button>
            <Button
              onClick={() => handleAction("shade")}
              className="bg-gray-500 hover:bg-gray-600 py-3"
            >
              ☂️ {currentData.actions.shade}
            </Button>
            <Button
              onClick={() => handleAction("move_sun")}
              className="bg-yellow-500 hover:bg-yellow-600 py-3"
            >
              ☀️ {currentData.actions.move_sun}
            </Button>
          </div>

          <div className="mt-6 text-center text-sm text-gray-600">
            Acciones: {actions}/{maxActions}
          </div>
        </div>
      </div>
    </div>
  );
}

src/components/games/SnoopyPathGame.tsx.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import confetti from "canvas-confetti";
// Usamos una imagen SVG de Snoopy directamente en línea
const snoopyImageSvg = `data:image/svg+xml;base64,${btoa(`
<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100">
  <circle cx="50" cy="40" r="25" fill="#FFFFFF" stroke="#000" stroke-width="2"/>
  <circle cx="42" cy="35" r="3" fill="#000"/>
  <circle cx="58" cy="35" r="3" fill="#000"/>
  <ellipse cx="50" cy="45" rx="2" ry="3" fill="#000"/>
  <path d="M 45 50 Q 50 55 55 50" stroke="#000" stroke-width="1.5" fill="none"/>
  <ellipse cx="30" cy="25" rx="8" ry="12" fill="#000"/>
  <ellipse cx="70" cy="25" rx="8" ry="12" fill="#000"/>
  <ellipse cx="50" cy="70" rx="15" ry="20" fill="#FFFFFF" stroke="#000" stroke-width="2"/>
  <circle cx="45" cy="75" r="2" fill="#000"/>
  <circle cx="55" cy="75" r="2" fill="#000"/>
</svg>
`)}`;

interface SnoopyPathGameProps {
  language: string;
  onComplete: (success: boolean) => void;
}

export default function SnoopyPathGame({ language, onComplete }: SnoopyPathGameProps) {
  const [currentLevel, setCurrentLevel] = useState(0);
  const [userAnswer, setUserAnswer] = useState("");
  const [snoopyPosition, setSnoopyPosition] = useState(10);
  const [showFeedback, setShowFeedback] = useState(false);
  const [isCorrect, setIsCorrect] = useState(false);

  const mathProblems = [
    { problem: "3 + 4", answer: "seven", answerNum: 7 },
    { problem: "9 - 2", answer: "seven", answerNum: 7 },
    { problem: "2 × 5", answer: "ten", answerNum: 10 },
    { problem: "12 ÷ 3", answer: "four", answerNum: 4 },
    { problem: "6 + 5", answer: "eleven", answerNum: 11 },
    { problem: "15 - 3", answer: "twelve", answerNum: 12 },
    { problem: "3 × 4", answer: "twelve", answerNum: 12 },
    { problem: "20 ÷ 4", answer: "five", answerNum: 5 }
  ];

  // Números en diferentes idiomas
  const numbers = {
    spanish: {
      1: "uno", 2: "dos", 3: "tres", 4: "cuatro", 5: "cinco",
      6: "seis", 7: "siete", 8: "ocho", 9: "nueve", 10: "diez",
      11: "once", 12: "doce", 13: "trece", 14: "catorce", 15: "quince",
      16: "dieciséis", 17: "diecisiete", 18: "dieciocho", 19: "diecinueve", 20: "veinte"
    },
    french: {
      1: "un", 2: "deux", 3: "trois", 4: "quatre", 5: "cinq",
      6: "six", 7: "sept", 8: "huit", 9: "neuf", 10: "dix",
      11: "onze", 12: "douze", 13: "treize", 14: "quatorze", 15: "quinze",
      16: "seize", 17: "dix-sept", 18: "dix-huit", 19: "dix-neuf", 20: "vingt"
    },
    italian: {
      1: "uno", 2: "due", 3: "tre", 4: "quattro", 5: "cinque",
      6: "sei", 7: "sette", 8: "otto", 9: "nove", 10: "dieci",
      11: "undici", 12: "dodici", 13: "tredici", 14: "quattordici", 15: "quindici",
      16: "sedici", 17: "diciassette", 18: "diciotto", 19: "diciannove", 20: "venti"
    },
    portuguese: {
      1: "um", 2: "dois", 3: "três", 4: "quatro", 5: "cinco",
      6: "seis", 7: "sete", 8: "oito", 9: "nove", 10: "dez",
      11: "onze", 12: "doze", 13: "treze", 14: "quatorze", 15: "quinze",
      16: "dezesseis", 17: "dezessete", 18: "dezoito", 19: "dezenove", 20: "vinte"
    },
    english: {
      1: "one", 2: "two", 3: "three", 4: "four", 5: "five",
      6: "six", 7: "seven", 8: "eight", 9: "nine", 10: "ten",
      11: "eleven", 12: "twelve", 13: "thirteen", 14: "fourteen", 15: "fifteen",
      16: "sixteen", 17: "seventeen", 18: "eighteen", 19: "nineteen", 20: "twenty"
    },
    russian: {
      1: "один", 2: "два", 3: "три", 4: "четыре", 5: "пять",
      6: "шесть", 7: "семь", 8: "восемь", 9: "девять", 10: "десять",
      11: "одиннадцать", 12: "двенадцать", 13: "тринадцать", 14: "четырнадцать", 15: "пятнадцать",
      16: "шестнадцать", 17: "семнадцать", 18: "восемнадцать", 19: "девятнадцать", 20: "двадцать"
    }
  };

  const currentProblem = mathProblems[currentLevel];
  const correctAnswer = numbers[language as keyof typeof numbers]?.[currentProblem.answerNum as keyof typeof numbers.english] || currentProblem.answer;

  const handleSubmit = () => {
    const correct = userAnswer.toLowerCase().trim() === correctAnswer.toLowerCase();
    setIsCorrect(correct);
    setShowFeedback(true);

    if (correct) {
      // Mover Snoopy hacia la meta
      setSnoopyPosition(snoopyPosition + 10);

      // Efecto de confetti para celebrar
      setTimeout(() => {
        confetti({
          particleCount: 100,
          spread: 70,
          origin: { y: 0.6 }
        });
      }, 500);

      setTimeout(() => {
        if (currentLevel < mathProblems.length - 1) {
          setCurrentLevel(currentLevel + 1);
          setUserAnswer("");
          setShowFeedback(false);
        } else {
          // Juego completado
          confetti({
            particleCount: 200,
            spread: 100,
            origin: { y: 0.6 }
          });
          onComplete(true);
        }
      }, 2500);
    } else {
      setTimeout(() => {
        setShowFeedback(false);
        setUserAnswer("");
      }, 3000);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-300 via-green-300 to-yellow-400 relative overflow-hidden">
      {/* Camino */}
      <div className="absolute bottom-20 w-full h-32 bg-gradient-to-t from-amber-700 to-amber-500 border-t-4 border-amber-800"></div>

      {/* Obstáculos matemáticos en el camino */}
      <div className="absolute bottom-32 left-1/4 w-12 h-12 bg-red-600 rounded-lg flex items-center justify-center text-white font-bold shadow-lg">
        ×
      </div>
      <div className="absolute bottom-32 left-1/2 w-12 h-12 bg-purple-600 rounded-lg flex items-center justify-center text-white font-bold shadow-lg">
        ÷
      </div>
      <div className="absolute bottom-32 left-3/4 w-12 h-12 bg-blue-600 rounded-lg flex items-center justify-center text-white font-bold shadow-lg">
        +
      </div>

      {/* Snoopy personalizado */}
      <div 
        className="absolute bottom-24 transition-all duration-1000 transform hover:scale-110"
        style={{ left: `${snoopyPosition}%` }}
      >
        <div className="w-16 h-16 rounded-full border-4 border-white shadow-lg bg-yellow-100 flex items-center justify-center overflow-hidden">
          <img 
            src={snoopyImageSvg} 
            alt="Snoopy" 
            className="w-12 h-12 object-contain"
          />
        </div>
        {/* Globo de diálogo */}
        <div className="absolute -top-12 -left-8 bg-white rounded-lg p-2 shadow-lg border-2 border-gray-300 text-xs font-bold text-gray-700">
          ¡Ayúdame!
          <div className="absolute -bottom-1 left-6 w-0 h-0 border-l-4 border-r-4 border-t-4 border-transparent border-t-white"></div>
        </div>
      </div>

      {/* Meta */}
      <div className="absolute bottom-20 right-10 text-6xl">
        🏁
      </div>

      {/* Panel de juego */}
      <div className="absolute top-20 left-1/2 transform -translate-x-1/2 bg-white/95 backdrop-blur-sm rounded-2xl p-8 shadow-2xl max-w-md w-full mx-4 border-4 border-yellow-400">
        <div className="text-center mb-6">
          <h2 className="text-3xl font-bold mb-2 text-blue-800">🐕 ¡Ayuda a Snoopy! 🛤️</h2>
          <p className="text-gray-700 font-medium">Snoopy necesita tu ayuda para encontrar el camino correcto.</p>
          <p className="text-sm text-gray-600 mt-2">Resuelve las operaciones matemáticas y escribe el resultado en palabras</p>
          <div className="text-xs text-orange-600 font-bold mt-1">
            Nivel {currentLevel + 1} de {mathProblems.length}
          </div>
        </div>

        <div className="bg-blue-100 rounded-lg p-4 mb-6 text-center">
          <div className="text-3xl font-bold text-blue-800 mb-2">
            {currentProblem.problem} = ?
          </div>
          <div className="text-sm text-gray-600">
            Escribe la respuesta en {language === 'spanish' ? 'español' :
                                    language === 'french' ? 'francés' : 
                                    language === 'italian' ? 'italiano' : 
                                    language === 'portuguese' ? 'portugués' : 
                                    language === 'english' ? 'inglés' : 'ruso'}
          </div>
        </div>

        <div className="space-y-4">
          <Input
            value={userAnswer}
            onChange={(e) => setUserAnswer(e.target.value)}
            placeholder="Escribe tu respuesta aquí..."
            className="text-center text-lg border-2 border-blue-300 focus:border-blue-500"
            onKeyPress={(e) => e.key === 'Enter' && handleSubmit()}
          />

          <Button 
            onClick={handleSubmit}
            disabled={!userAnswer.trim() || showFeedback}
            className="w-full bg-blue-600 hover:bg-blue-700 text-white font-bold py-3 text-lg shadow-lg transform hover:scale-105 transition-all"
          >
            🎯 Ayudar a Snoopy
          </Button>
        </div>

        {/* Feedback */}
        {showFeedback && (
          <div className={`mt-4 p-4 rounded-lg text-center font-bold ${
            isCorrect 
              ? 'bg-green-100 text-green-800 border-2 border-green-400' 
              : 'bg-red-100 text-red-800 border-2 border-red-400'
          }`}>
            {isCorrect ? (
              <div>
                <div className="text-2xl mb-2">🎉 ¡Excelente!</div>
                <div>Snoopy avanza por el camino correcto</div>
              </div>
            ) : (
              <div>
                <div className="text-2xl mb-2">❌ Intenta de nuevo</div>
                <div>La respuesta correcta es: <strong>{correctAnswer}</strong></div>
              </div>
            )}
          </div>
        )}

        {/* Barra de progreso */}
        <div className="mt-6">
          <div className="bg-gray-200 rounded-full h-4 shadow-inner">
            <div 
              className="bg-gradient-to-r from-green-400 to-blue-500 h-4 rounded-full transition-all duration-1000 shadow-lg"
              style={{ width: `${((currentLevel + 1) / mathProblems.length) * 100}%` }}
            ></div>
          </div>
          <div className="text-center text-sm text-gray-600 mt-2">
            Progreso: {currentLevel + 1} / {mathProblems.length}
          </div>
        </div>
      </div>
    </div>
  );
}

src/components/games/SpidermanTrainGame..content:
import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";

interface SpidermanTrainGameProps {
  language: string;
  onComplete: (success: boolean) => void;
}

export default function SpidermanTrainGame({ language, onComplete }: SpidermanTrainGameProps) {
  const [currentLevel, setCurrentLevel] = useState(0);
  const [attempts, setAttempts] = useState(0);
  const [showFeedback, setShowFeedback] = useState(false);
  const [isCorrect, setIsCorrect] = useState(false);
  const [timeLeft, setTimeLeft] = useState(30);

  const directions = {
    french: [
      { correct: "À gauche", wrong: ["À gauce", "À gaushe"], trap: "Faltas de ortografía" },
      { correct: "À droite", wrong: ["À droitté", "À droit"], trap: "Doble consonante incorrecta" },
      { correct: "Tout droit", wrong: ["Tous droit", "Tout droi"], trap: "Concordancia incorrecta" },
      { correct: "En haut", wrong: ["En hauts", "En aut"], trap: "Pluralización incorrecta" },
      { correct: "En bas", wrong: ["En bass", "En ba"], trap: "Doble consonante" }
    ],
    italian: [
      { correct: "A sinistra", wrong: ["A sinisttra", "A sinistra"], trap: "Doble consonante" },
      { correct: "A destra", wrong: ["A desstra", "A desttra"], trap: "Consonantes dobles" },
      { correct: "Dritto", wrong: ["Drito", "Dritta"], trap: "Consonante faltante" },
      { correct: "In alto", wrong: ["In altto", "In altu"], trap: "Doble consonante" },
      { correct: "In basso", wrong: ["In baso", "In basso"], trap: "Consonante simple" }
    ],
    english: [
      { correct: "Left", wrong: ["Lef", "Leftt"], trap: "Letter missing/extra" },
      { correct: "Right", wrong: ["Rigth", "Rigt"], trap: "Letter order" },
      { correct: "Straight", wrong: ["Straigth", "Straigt"], trap: "Silent letters" },
      { correct: "Up", wrong: ["Upp", "Pu"], trap: "Double letters" },
      { correct: "Down", wrong: ["Donw", "Dwn"], trap: "Letter order" }
    ]
  };

  const currentData = directions[language as keyof typeof directions] || directions.english;
  const currentDirection = currentData[currentLevel];

  useEffect(() => {
    if (timeLeft > 0) {
      const timer = setTimeout(() => setTimeLeft(timeLeft - 1), 1000);
      return () => clearTimeout(timer);
    } else {
      onComplete(false);
    }
  }, [timeLeft, onComplete]);

  const handleChoice = (choice: string) => {
    if (showFeedback) return;

    const correct = choice === currentDirection.correct;
    setIsCorrect(correct);
    setShowFeedback(true);

    if (correct) {
      setTimeout(() => {
        if (currentLevel < currentData.length - 1) {
          setCurrentLevel(currentLevel + 1);
          setShowFeedback(false);
          setAttempts(0);
          setTimeLeft(30);
        } else {
          onComplete(true);
        }
      }, 2000);
    } else {
      setAttempts(attempts + 1);
      if (attempts >= 4) {
        setTimeout(() => onComplete(false), 2000);
      } else {
        setTimeout(() => setShowFeedback(false), 2000);
      }
    }
  };

  const allOptions = [currentDirection.correct, ...currentDirection.wrong].sort(() => Math.random() - 0.5);

  return (
    <div className="min-h-screen bg-gradient-to-b from-red-500 to-pink-600 relative overflow-hidden">
      {/* Background city */}
      <div className="absolute inset-0 opacity-20">
        <div className="absolute bottom-0 w-full h-64 bg-gradient-to-t from-gray-800 to-gray-600"></div>
        <div className="absolute bottom-32 left-10 w-16 h-32 bg-gray-700"></div>
        <div className="absolute bottom-32 left-32 w-20 h-40 bg-gray-700"></div>
        <div className="absolute bottom-32 right-10 w-16 h-28 bg-gray-700"></div>
        <div className="absolute bottom-32 right-32 w-18 h-36 bg-gray-700"></div>
      </div>

      {/* Spiderman */}
      <div className="absolute top-20 left-1/4 text-6xl animate-bounce">
        🕷️
      </div>

      {/* Train */}
      <div className="absolute bottom-40 right-10 text-6xl">
        🚂
      </div>

      {/* Danger zone */}
      <div className="absolute bottom-32 right-0 w-32 h-8 bg-red-800 opacity-80"></div>

      {/* Game panel */}
      <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 bg-white rounded-2xl p-8 shadow-2xl max-w-md w-full mx-4">
        <div className="text-center mb-6">
          <h2 className="text-2xl font-bold mb-2">¡Salva a Spiderman!</h2>
          <p className="text-gray-600 text-sm">Elige la dirección correcta</p>
          <div className="mt-2 flex justify-between text-sm">
            <span>Tiempo: {timeLeft}s</span>
            <span>Intentos: {5 - attempts}/5</span>
          </div>
        </div>

        <div className="bg-red-100 rounded-lg p-4 mb-6 text-center">
          <div className="text-lg font-bold text-red-800 mb-2">
            "¡Ayúdame! ¿Hacia dónde debo ir?"
          </div>
          <div className="text-sm text-gray-600">
            ⚠️ {currentDirection.trap}
          </div>
        </div>

        {!showFeedback ? (
          <div className="space-y-3">
            {allOptions.map((option, index) => (
              <Button
                key={index}
                onClick={() => handleChoice(option)}
                className="w-full bg-red-600 hover:bg-red-700 text-white py-3 text-lg"
              >
                {option}
              </Button>
            ))}
          </div>
        ) : (
          <div className={`text-center p-4 rounded-lg ${
            isCorrect ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
          }`}>
            <div className="text-4xl mb-2">
              {isCorrect ? "🎉" : "❌"}
            </div>
            <p className="font-bold">
              {isCorrect 
                ? "¡Correcto! Spiderman está a salvo!" 
                : `¡Incorrecto! La respuesta era: ${currentDirection.correct}`
              }
            </p>
          </div>
        )}

        <div className="mt-4 text-center text-sm text-gray-600">
          Nivel {currentLevel + 1} de {currentData.length}
        </div>
      </div>
    </div>
  );
}

src/components/games/TreasureHuntGame.ts.content:
import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Clock, Heart, Map } from "lucide-react";
import GameScoreTracker from "@/components/GameScoreTracker";

interface TreasureHuntGameProps {
  language: string;
  onComplete: (success: boolean) => void;
}

const treasureData = {
  french: [
    {
      instruction: "Pour commencer, allez vers le ____",
      options: ["nord", "sud", "est"],
      correct: 0,
      icon: "🧭"
    },
    {
      instruction: "Maintenant, tournez à ____",
      options: ["gauche", "droite", "derrière"],
      correct: 1,
      icon: "↪️"
    },
    {
      instruction: "Continuez tout ____",
      options: ["droit", "courbe", "cercle"],
      correct: 0,
      icon: "⬆️"
    },
    {
      instruction: "Le trésor est ____ l'arbre",
      options: ["sur", "sous", "dans"],
      correct: 1,
      icon: "🌳"
    }
  ],
  italian: [
    {
      instruction: "Per iniziare, andate verso ____",
      options: ["nord", "sud", "ovest"],
      correct: 0,
      icon: "🧭"
    },
    {
      instruction: "Ora, girate a ____",
      options: ["sinistra", "destra", "dietro"],
      correct: 0,
      icon: "↪️"
    },
    {
      instruction: "Continuate ____",
      options: ["dritto", "curvo", "tondo"],
      correct: 0,
      icon: "⬆️"
    },
    {
      instruction: "Il tesoro è ____ la roccia",
      options: ["sopra", "sotto", "dentro"],
      correct: 1,
      icon: "🪨"
    }
  ],
  portuguese: [
    {
      instruction: "Para começar, vão para o ____",
      options: ["norte", "sul", "leste"],
      correct: 0,
      icon: "🧭"
    },
    {
      instruction: "Agora, virem à ____",
      options: ["esquerda", "direita", "trás"],
      correct: 1,
      icon: "↪️"
    },
    {
      instruction: "Continuem ____",
      options: ["reto", "curva", "círculo"],
      correct: 0,
      icon: "⬆️"
    },
    {
      instruction: "O tesouro está ____ da ponte",
      options: ["em cima", "embaixo", "dentro"],
      correct: 1,
      icon: "🌉"
    }
  ],
  english: [
    {
      instruction: "To start, go ____",
      options: ["north", "south", "west"],
      correct: 0,
      icon: "🧭"
    },
    {
      instruction: "Now, turn ____",
      options: ["left", "right", "back"],
      correct: 0,
      icon: "↪️"
    },
    {
      instruction: "Continue ____",
      options: ["straight", "curved", "round"],
      correct: 0,
      icon: "⬆️"
    },
    {
      instruction: "The treasure is ____ the cave",
      options: ["above", "inside", "outside"],
      correct: 1,
      icon: "🕳️"
    }
  ],
  russian: [
    {
      instruction: "Чтобы начать, идите на ____",
      options: ["север", "юг", "запад"],
      correct: 0,
      icon: "🧭"
    },
    {
      instruction: "Теперь поверните ____",
      options: ["налево", "направо", "назад"],
      correct: 0,
      icon: "↪️"
    },
    {
      instruction: "Продолжайте ____",
      options: ["прямо", "кривая", "круг"],
      correct: 0,
      icon: "⬆️"
    },
    {
      instruction: "Сокровище ____ дерева",
      options: ["над", "под", "в"],
      correct: 1,
      icon: "🌳"
    }
  ]
};

export default function TreasureHuntGame({ language, onComplete }: TreasureHuntGameProps) {
  const [currentStep, setCurrentStep] = useState(0);
  const [selectedOption, setSelectedOption] = useState<number | null>(null);
  const [showResult, setShowResult] = useState(false);
  const [score, setScore] = useState(0);
  const [attempts, setAttempts] = useState(5);
  const [timeLeft, setTimeLeft] = useState(120); // 2 minutos
  const [gameStarted, setGameStarted] = useState(false);
  const [gameEnded, setGameEnded] = useState(false);
  const [isGameActive, setIsGameActive] = useState(false);
  const [mapPosition, setMapPosition] = useState({ x: 50, y: 80 });

  const directions = treasureData[language as keyof typeof treasureData];
  const currentDirection = directions[currentStep];

  // Temporizador
  useEffect(() => {
    if (!gameStarted || gameEnded || timeLeft <= 0) return;

    const timer = setInterval(() => {
      setTimeLeft(prev => {
        if (prev <= 1) {
          setGameEnded(true);
          setIsGameActive(false);
          onComplete(false);
          return 0;
        }
        return prev - 1;
      });
    }, 1000);

    return () => clearInterval(timer);
  }, [gameStarted, gameEnded, timeLeft, onComplete]);

  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  const handleOptionClick = (optionIndex: number) => {
    if (showResult || gameEnded) return;
    setSelectedOption(optionIndex);
  };

  const confirmDirection = () => {
    if (selectedOption === null || gameEnded) return;

    setShowResult(true);
    const isCorrect = selectedOption === currentDirection.correct;

    if (isCorrect) {
      // Add points for correct direction
      const pointsEarned = 150 + (timeLeft * 3); // Base points + time bonus
      setScore(prev => prev + pointsEarned);

      // Update global score tracker
      if (window.updateGameScore) {
        window.updateGameScore(pointsEarned);
      }

      // Mover en el mapa
      const newPosition = { ...mapPosition };
      if (currentStep === 0) newPosition.y -= 15; // norte
      else if (currentStep === 1) newPosition.x += 15; // derecha
      else if (currentStep === 2) newPosition.y -= 10; // continuar

      setMapPosition(newPosition);

      setTimeout(() => {
        if (currentStep < directions.length - 1) {
          setCurrentStep(prev => prev + 1);
          setSelectedOption(null);
          setShowResult(false);
        } else {
          setGameEnded(true);
          setIsGameActive(false);
          onComplete(true);
        }
      }, 2000);
    } else {
      setAttempts(prev => {
        const newAttempts = prev - 1;
        if (newAttempts <= 0) {
          setTimeout(() => {
            setGameEnded(true);
            setIsGameActive(false);
            onComplete(false);
          }, 2000);
        } else {
          setTimeout(() => {
            setSelectedOption(null);
            setShowResult(false);
          }, 2000);
        }
        return newAttempts;
      });
    }
  };

  const startGame = () => {
    setGameStarted(true);
  };

  // Pantalla inicial
  if (!gameStarted) {
    return (
      <div className="min-h-screen bg-gradient-to-b from-amber-400 to-orange-600 flex items-center justify-center">
        <div className="bg-white/90 backdrop-blur-sm rounded-2xl p-8 text-center max-w-md shadow-2xl">
          <div className="text-6xl mb-6">🗺️</div>
          <h2 className="text-2xl font-bold mb-4 text-gray-800">Búsqueda del Tesoro</h2>
          <p className="text-gray-600 mb-6">
            Tienes 2 minutos y 5 intentos para seguir las {directions.length} direcciones correctamente.
            ¡Encuentra el tesoro siguiendo las pistas en el mapa!
          </p>
          <Button 
            onClick={startGame} 
            className="bg-amber-600 hover:bg-amber-700 text-white px-8 py-3 text-lg"
          >
            ¡Comenzar Búsqueda!
          </Button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-b from-amber-400 to-orange-600 relative overflow-hidden">
      {/* HUD */}
      <div className="absolute top-4 left-4 right-4 flex justify-between items-center z-20">
        <div className="bg-white/90 rounded-lg px-4 py-2 flex items-center gap-2">
          <Clock className="w-5 h-5 text-blue-600" />
          <span className={`font-bold ${timeLeft <= 30 ? 'text-red-600 animate-pulse' : 'text-gray-800'}`}>
            {formatTime(timeLeft)}
          </span>
        </div>

        <div className="bg-white/90 rounded-lg px-4 py-2 flex items-center gap-2">
          <Map className="w-5 h-5 text-amber-600" />
          <span className="text-gray-800 font-semibold">Paso {currentStep + 1}/{directions.length}</span>
        </div>

        <div className="bg-white/90 rounded-lg px-4 py-2 flex items-center gap-2">
          <span className="text-orange-600 font-bold">🏆 {score}</span>
        </div>

        <div className="bg-white/90 rounded-lg px-4 py-2 flex items-center gap-2">
          <Heart className="w-5 h-5 text-red-500" />
          <span className="font-bold text-gray-800">{attempts}</span>
        </div>
      </div>

      {/* Mapa del tesoro */}
      <div className="absolute top-20 left-1/2 transform -translate-x-1/2 w-80 h-60 bg-yellow-200 rounded-lg border-4 border-amber-800 shadow-lg">
        {/* Elementos del mapa */}
        <div className="absolute top-4 left-4 text-2xl">🏔️</div>
        <div className="absolute top-4 right-4 text-2xl">🌳</div>
        <div className="absolute bottom-4 left-4 text-2xl">🏞️</div>
        <div className="absolute bottom-4 right-4 text-2xl">🕳️</div>

        {/* Tesoro */}
        {currentStep === directions.length - 1 && showResult && selectedOption === currentDirection.correct && (
          <div className="absolute top-1/2 right-1/4 text-3xl animate-bounce">💰</div>
        )}

        {/* Posición del jugador */}
        <div 
          className="absolute w-6 h-6 bg-red-500 rounded-full border-2 border-white transition-all duration-1000"
          style={{
            left: `${mapPosition.x}%`,
            top: `${mapPosition.y}%`,
            transform: 'translate(-50%, -50%)'
          }}
        >
          <div className="absolute inset-0 flex items-center justify-center text-white text-xs font-bold">
            👤
          </div>
        </div>
      </div>

      {/* Instrucción actual */}
      <div className="absolute top-96 left-1/2 transform -translate-x-1/2 bg-white/90 rounded-lg p-6 text-center max-w-md">
        <div className="text-3xl mb-4">{currentDirection.icon}</div>
        <h3 className="text-lg font-bold mb-4 text-gray-800">Dirección:</h3>
        <p className="text-xl mb-4">{currentDirection.instruction}</p>

        {/* Opciones */}
        <div className="grid grid-cols-1 gap-3">
          {currentDirection.options.map((option, index) => (
            <button
              key={index}
              onClick={() => handleOptionClick(index)}
              className={`px-4 py-3 rounded-lg border-2 transition-all ${
                selectedOption === index
                  ? 'border-amber-500 bg-amber-100 text-amber-800'
                  : 'border-gray-300 bg-white hover:border-amber-300'
              }`}
              disabled={showResult || gameEnded}
            >
              {option}
            </button>
          ))}
        </div>
      </div>

      {/* Botón confirmar */}
      <div className="absolute bottom-20 left-1/2 transform -translate-x-1/2">
        <Button
          onClick={confirmDirection}
          disabled={selectedOption === null || showResult || gameEnded}
          className="bg-amber-600 hover:bg-amber-700 text-white font-bold px-8 py-3 text-lg shadow-lg disabled:opacity-50"
        >
          ¡Seguir Dirección! 🗺️
        </Button>
      </div>

      {/* Mensaje de resultado */}
      {showResult && (
        <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 bg-white/95 rounded-xl p-6 text-center shadow-2xl z-30">
          <div className="text-4xl mb-2">
            {selectedOption === currentDirection.correct ? '✅' : '❌'}
          </div>
          <h3 className="text-xl font-bold mb-2 text-gray-800">
            {selectedOption === currentDirection.correct ? '¡Correcto!' : '¡Incorrecto!'}
          </h3>
          <p className="text-gray-600">
            {selectedOption === currentDirection.correct 
              ? currentStep === directions.length - 1 ? '¡Has encontrado el tesoro!' : 'Continúa siguiendo el mapa...'
              : attempts > 1 ? 'Inténtalo de nuevo...' : '¡Se acabaron los intentos!'
            }
          </p>
        </div>
      )}

      {/* Game Score Tracker */}
      {gameStarted && (
        <div className="absolute top-4 left-1/2 transform -translate-x-1/2 z-30">
          <GameScoreTracker
            gameId="treasure-hunt"
            gameName="Búsqueda del Tesoro"
            difficulty="Avanzado"
            language={language}
            isGameActive={isGameActive}
            onGameStart={() => {
              setIsGameActive(true);
              setScore(0);
            }}
            onGameEnd={(finalScore, completionTime) => {
              console.log(`Treasure hunt completed with score: ${finalScore}, time: ${completionTime}s`);
            }}
          />
        </div>
      )}
    </div>
  );
}

src/components/games/VacationRoutineGame.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { gameData } from "@/data/games";

interface VacationRoutineGameProps {
  language: string;
  onComplete: (success: boolean) => void;
}

export default function VacationRoutineGame({ language, onComplete }: VacationRoutineGameProps) {
  const [currentTask, setCurrentTask] = useState(0);
  const [selectedAnswers, setSelectedAnswers] = useState<number[]>([]);
  const [showThankYou, setShowThankYou] = useState(false);

  const gameInfo = gameData.vacation[language as keyof typeof gameData.vacation];

  if (!gameInfo) {
    // Datos por defecto si el idioma no está configurado
    const defaultGame = {
      routine: "Hello! Help me finish my vacation routine!",
      tasks: [
        { text: "I _____ at 9am", options: ["wake up", "sleep", "eat"], correct: 0 },
        { text: "I brush my _____", options: ["hair", "teeth", "hands"], correct: 1 },
        { text: "I _____ breakfast", options: ["dinner", "lunch", "have"], correct: 2 }
      ],
      thanks: "Thank you so much! Now I can enjoy my vacation!"
    };

    const handleAnswer = (answerIndex: number) => {
      const newAnswers = [...selectedAnswers, answerIndex];
      setSelectedAnswers(newAnswers);

      if (currentTask < defaultGame.tasks.length - 1) {
        setTimeout(() => setCurrentTask(currentTask + 1), 1000);
      } else {
        setTimeout(() => {
          setShowThankYou(true);
          const allCorrect = newAnswers.every((answer, index) => answer === defaultGame.tasks[index].correct);
          setTimeout(() => onComplete(allCorrect), 2000);
        }, 1000);
      }
    };

    return (
      <div className="min-h-screen bg-gradient-to-b from-blue-300 to-blue-500 flex items-center justify-center p-6">
        <div className="bg-white rounded-2xl p-8 max-w-2xl w-full text-center shadow-2xl">
          {!showThankYou ? (
            <>
              <div className="text-6xl mb-4">👦</div>
              <h2 className="text-2xl font-bold mb-4 text-blue-800">{defaultGame.routine}</h2>

              <div className="bg-gray-100 rounded-lg p-6 mb-6">
                <div className="text-lg mb-4 text-gray-800">
                  {defaultGame.tasks[currentTask].text}
                </div>

                <div className="grid grid-cols-1 gap-3">
                  {defaultGame.tasks[currentTask].options.map((option, index) => (
                    <Button
                      key={index}
                      onClick={() => handleAnswer(index)}
                      className="bg-blue-500 hover:bg-blue-600 text-white py-3 text-lg"
                    >
                      {option}
                    </Button>
                  ))}
                </div>
              </div>

              <div className="text-sm text-gray-600">
                Tarea {currentTask + 1} de {defaultGame.tasks.length}
              </div>
            </>
          ) : (
            <div className="text-center">
              <div className="text-6xl mb-4">😊</div>
              <h2 className="text-2xl font-bold mb-4 text-green-600">{defaultGame.thanks}</h2>
              <div className="text-4xl">🎉</div>
            </div>
          )}
        </div>
      </div>
    );
  }

  const handleAnswer = (answerIndex: number) => {
    const newAnswers = [...selectedAnswers, answerIndex];
    setSelectedAnswers(newAnswers);

    if (currentTask < gameInfo.tasks.length - 1) {
      setTimeout(() => setCurrentTask(currentTask + 1), 1000);
    } else {
      setTimeout(() => {
        setShowThankYou(true);
        const allCorrect = newAnswers.every((answer, index) => answer === gameInfo.tasks[index].correct);
        setTimeout(() => onComplete(allCorrect), 2000);
      }, 1000);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-300 to-blue-500 flex items-center justify-center p-6">
      <div className="bg-white rounded-2xl p-8 max-w-2xl w-full text-center shadow-2xl">
        {!showThankYou ? (
          <>
            <div className="text-6xl mb-4">👦</div>
            <h2 className="text-2xl font-bold mb-4 text-blue-800">{gameInfo.routine}</h2>

            <div className="bg-gray-100 rounded-lg p-6 mb-6">
              <div className="text-lg mb-4 text-gray-800">
                {gameInfo.tasks[currentTask].text}
              </div>

              <div className="grid grid-cols-1 gap-3">
                {gameInfo.tasks[currentTask].options.map((option, index) => (
                  <Button
                    key={index}
                    onClick={() => handleAnswer(index)}
                    className="bg-blue-500 hover:bg-blue-600 text-white py-3 text-lg"
                  >
                    {option}
                  </Button>
                ))}
              </div>
            </div>

            <div className="text-sm text-gray-600">
              Tarea {currentTask + 1} de {gameInfo.tasks.length}
            </div>
          </>
        ) : (
          <div className="text-center">
            <div className="text-6xl mb-4">😊</div>
            <h2 className="text-2xl font-bold mb-4 text-green-600">{gameInfo.thanks}</h2>
            <div className="text-4xl">🎉</div>
          </div>
        )}
      </div>
    </div>
  );
}

src/components/games/WordSoupGame.tsx.content:
import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Clock, Search, Target } from "lucide-react";

interface WordSoupGameProps {
  language: string;
  onComplete: (success: boolean) => void;
}

interface WordToFind {
  word: string;
  translation: string;
  found: boolean;
  positions?: { row: number; col: number; direction: string }[];
}

const wordsData = {
  french: [
    { word: "PARIS", translation: "París" },
    { word: "BAGUETTE", translation: "Baguette" },
    { word: "AMOUR", translation: "Amor" },
    { word: "CAFE", translation: "Café" },
    { word: "MUSEE", translation: "Museo" },
    { word: "SEINE", translation: "Sena" },
    { word: "CROISSANT", translation: "Croissant" },
    { word: "TOUR", translation: "Torre" },
    { word: "FROMAGE", translation: "Queso" },
    { word: "VIN", translation: "Vino" }
  ],
  italian: [
    { word: "ROMA", translation: "Roma" },
    { word: "PASTA", translation: "Pasta" },
    { word: "AMORE", translation: "Amor" },
    { word: "PIZZA", translation: "Pizza" },
    { word: "GELATO", translation: "Helado" },
    { word: "VENICE", translation: "Venecia" },
    { word: "OPERA", translation: "Ópera" },
    { word: "VINO", translation: "Vino" },
    { word: "MARE", translation: "Mar" },
    { word: "SOLE", translation: "Sol" }
  ],
  portuguese: [
    { word: "BRASIL", translation: "Brasil" },
    { word: "SAMBA", translation: "Samba" },
    { word: "PRAIA", translation: "Playa" },
    { word: "FUTEBOL", translation: "Fútbol" },
    { word: "CARNAVAL", translation: "Carnaval" },
    { word: "IPANEMA", translation: "Ipanema" },
    { word: "CAIPIRINHA", translation: "Caipirinha" },
    { word: "AMAZONIA", translation: "Amazonía" },
    { word: "FEIJOADA", translation: "Feijoada" },
    { word: "BOSSA", translation: "Bossa" }
  ],
  english: [
    { word: "LONDON", translation: "Londres" },
    { word: "TEA", translation: "Té" },
    { word: "FOOTBALL", translation: "Fútbol" },
    { word: "QUEEN", translation: "Reina" },
    { word: "BRIDGE", translation: "Puente" },
    { word: "CASTLE", translation: "Castillo" },
    { word: "GARDEN", translation: "Jardín" },
    { word: "MUSIC", translation: "Música" },
    { word: "HISTORY", translation: "Historia" },
    { word: "CULTURE", translation: "Cultura" }
  ],
  russian: [
    { word: "МОСКВА", translation: "Moscú" },
    { word: "ВОДКА", translation: "Vodka" },
    { word: "БОРЩ", translation: "Borsch" },
    { word: "БАЛЕТ", translation: "Ballet" },
    { word: "КРЕМЛЬ", translation: "Kremlin" },
    { word: "ТРОЙКА", translation: "Troika" },
    { word: "МАТРЁШКА", translation: "Matrioshka" },
    { word: "САМОВАР", translation: "Samovar" },
    { word: "ПУШКИН", translation: "Pushkin" },
    { word: "ЗИМА", translation: "Invierno" }
  ]
};

export default function WordSoupGame({ language, onComplete }: WordSoupGameProps) {
  const [grid, setGrid] = useState<string[][]>([]);
  const [wordsToFind, setWordsToFind] = useState<WordToFind[]>([]);
  const [selectedCells, setSelectedCells] = useState<{row: number, col: number}[]>([]);
  const [foundWords, setFoundWords] = useState<string[]>([]);
  const [timeLeft, setTimeLeft] = useState(120); // 2 minutos
  const [gameStarted, setGameStarted] = useState(false);
  const [gameEnded, setGameEnded] = useState(false);
  const [isDragging, setIsDragging] = useState(false);

  const GRID_SIZE = 15;

  // Temporizador
  useEffect(() => {
    if (!gameStarted || gameEnded || timeLeft <= 0) return;

    const timer = setInterval(() => {
      setTimeLeft(prev => {
        if (prev <= 1) {
          setGameEnded(true);
          onComplete(false);
          return 0;
        }
        return prev - 1;
      });
    }, 1000);

    return () => clearInterval(timer);
  }, [gameStarted, gameEnded, timeLeft, onComplete]);

  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  // Generar la sopa de letras
  const generateWordSoup = () => {
    const words = wordsData[language as keyof typeof wordsData].slice(0, 10);
    const newGrid: string[][] = Array(GRID_SIZE).fill(null).map(() => Array(GRID_SIZE).fill(''));
    const placedWords: WordToFind[] = [];

    // Direcciones: horizontal, vertical, diagonal
    const directions = [
      [0, 1],   // horizontal derecha
      [1, 0],   // vertical abajo
      [1, 1],   // diagonal abajo-derecha
      [0, -1],  // horizontal izquierda
      [-1, 0],  // vertical arriba
      [-1, -1], // diagonal arriba-izquierda
      [1, -1],  // diagonal abajo-izquierda
      [-1, 1]   // diagonal arriba-derecha
    ];

    // Colocar palabras
    words.forEach(({ word, translation }) => {
      let placed = false;
      let attempts = 0;

      while (!placed && attempts < 100) {
        const direction = directions[Math.floor(Math.random() * directions.length)];
        const startRow = Math.floor(Math.random() * GRID_SIZE);
        const startCol = Math.floor(Math.random() * GRID_SIZE);

        // Verificar si la palabra cabe
        let canPlace = true;
        const positions: { row: number; col: number; direction: string }[] = [];

        for (let i = 0; i < word.length; i++) {
          const row = startRow + direction[0] * i;
          const col = startCol + direction[1] * i;

          if (row < 0 || row >= GRID_SIZE || col < 0 || col >= GRID_SIZE) {
            canPlace = false;
            break;
          }

          if (newGrid[row][col] !== '' && newGrid[row][col] !== word[i]) {
            canPlace = false;
            break;
          }

          positions.push({ row, col, direction: direction.join(',') });
        }

        if (canPlace) {
          // Colocar la palabra
          for (let i = 0; i < word.length; i++) {
            const row = startRow + direction[0] * i;
            const col = startCol + direction[1] * i;
            newGrid[row][col] = word[i];
          }

          placedWords.push({ 
            word, 
            translation, 
            found: false, 
            positions 
          });
          placed = true;
        }

        attempts++;
      }
    });

    // Llenar espacios vacíos con letras aleatorias
    const letters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ';
    for (let row = 0; row < GRID_SIZE; row++) {
      for (let col = 0; col < GRID_SIZE; col++) {
        if (newGrid[row][col] === '') {
          newGrid[row][col] = letters[Math.floor(Math.random() * letters.length)];
        }
      }
    }

    setGrid(newGrid);
    setWordsToFind(placedWords);
  };

  useEffect(() => {
    if (gameStarted) {
      generateWordSoup();
    }
  }, [gameStarted, language]);

  const handleCellMouseDown = (row: number, col: number) => {
    setIsDragging(true);
    setSelectedCells([{ row, col }]);
  };

  const handleCellMouseEnter = (row: number, col: number) => {
    if (isDragging) {
      setSelectedCells(prev => {
        const newSelection = [...prev];
        const lastCell = newSelection[newSelection.length - 1];

        // Solo permitir selección en línea recta
        if (newSelection.length === 1) {
          newSelection.push({ row, col });
        } else {
          // Calcular dirección y continuar en esa línea
          const deltaRow = lastCell.row - newSelection[0].row;
          const deltaCol = lastCell.col - newSelection[0].col;

          // Verificar si el nuevo punto está en la misma línea
          const newDeltaRow = row - newSelection[0].row;
          const newDeltaCol = col - newSelection[0].col;

          if (Math.abs(newDeltaRow) <= Math.abs(deltaRow) && Math.abs(newDeltaCol) <= Math.abs(deltaCol)) {
            return [newSelection[0], { row, col }];
          }
        }

        return newSelection;
      });
    }
  };

  const handleCellMouseUp = () => {
    if (selectedCells.length > 1) {
      checkSelectedWord();
    }
    setIsDragging(false);
    setSelectedCells([]);
  };

  const checkSelectedWord = () => {
    const selectedWord = selectedCells.map(({row, col}) => grid[row][col]).join('');
    const reversedWord = selectedWord.split('').reverse().join('');

    const foundWord = wordsToFind.find(word => 
      !word.found && (word.word === selectedWord || word.word === reversedWord)
    );

    if (foundWord) {
      setFoundWords(prev => [...prev, foundWord.word]);
      setWordsToFind(prev => 
        prev.map(word => 
          word.word === foundWord.word ? { ...word, found: true } : word
        )
      );

      // Verificar si se completó el juego
      const newFoundCount = foundWords.length + 1;
      if (newFoundCount === wordsToFind.length) {
        setGameEnded(true);
        onComplete(true);
      }
    }
  };

  const startGame = () => {
    setGameStarted(true);
  };

  // Pantalla inicial
  if (!gameStarted) {
    return (
      <div className="min-h-screen bg-gradient-to-b from-teal-400 to-green-600 flex items-center justify-center">
        <div className="bg-white/90 backdrop-blur-sm rounded-2xl p-8 text-center max-w-md shadow-2xl">
          <div className="text-6xl mb-6">🔤</div>
          <h2 className="text-2xl font-bold mb-4 text-gray-800">Sopa de Letras Cultural</h2>
          <p className="text-gray-600 mb-6">
            Tienes 2 minutos para encontrar 10 palabras culturales ocultas en la sopa de letras.
            ¡Arrastra para seleccionar las palabras!
          </p>
          <Button 
            onClick={startGame} 
            className="bg-teal-600 hover:bg-teal-700 text-white px-8 py-3 text-lg"
          >
            ¡Comenzar Búsqueda!
          </Button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-b from-teal-400 to-green-600 p-4">
      {/* HUD */}
      <div className="flex justify-between items-center mb-4">
        <div className="bg-white/90 rounded-lg px-4 py-2 flex items-center gap-2">
          <Clock className="w-5 h-5 text-blue-600" />
          <span className={`font-bold ${timeLeft <= 30 ? 'text-red-600 animate-pulse' : 'text-gray-800'}`}>
            {formatTime(timeLeft)}
          </span>
        </div>

        <div className="bg-white/90 rounded-lg px-4 py-2 flex items-center gap-2">
          <Search className="w-5 h-5 text-teal-600" />
          <span className="text-gray-800 font-semibold">{foundWords.length}/{wordsToFind.length}</span>
        </div>
      </div>

      <div className="flex gap-4 max-w-6xl mx-auto">
        {/* Sopa de letras */}
        <div className="flex-1">
          <div 
            className="grid gap-1 bg-white/90 p-4 rounded-xl shadow-lg"
            style={{ gridTemplateColumns: `repeat(${GRID_SIZE}, 1fr)` }}
          >
            {grid.map((row, rowIndex) =>
              row.map((letter, colIndex) => {
                const isSelected = selectedCells.some(cell => 
                  cell.row === rowIndex && cell.col === colIndex
                );
                const isFoundWord = wordsToFind.some(word => 
                  word.found && word.positions?.some(pos => 
                    pos.row === rowIndex && pos.col === colIndex
                  )
                );

                return (
                  <div
                    key={`${rowIndex}-${colIndex}`}
                    className={`w-8 h-8 flex items-center justify-center text-sm font-bold border cursor-pointer select-none transition-all ${
                      isSelected ? 'bg-teal-200 border-teal-400' :
                      isFoundWord ? 'bg-green-200 border-green-400' :
                      'bg-white border-gray-300 hover:bg-gray-100'
                    }`}
                    onMouseDown={() => handleCellMouseDown(rowIndex, colIndex)}
                    onMouseEnter={() => handleCellMouseEnter(rowIndex, colIndex)}
                    onMouseUp={handleCellMouseUp}
                  >
                    {letter}
                  </div>
                );
              })
            )}
          </div>
        </div>

        {/* Lista de palabras */}
        <div className="w-64">
          <div className="bg-white/90 rounded-xl p-4 shadow-lg">
            <h3 className="text-lg font-bold mb-4 text-gray-800 flex items-center gap-2">
              <Target className="w-5 h-5" />
              Palabras a encontrar:
            </h3>
            <div className="space-y-2">
              {wordsToFind.map((wordObj, index) => (
                <div
                  key={index}
                  className={`p-2 rounded-lg border transition-all ${
                    wordObj.found 
                      ? 'bg-green-100 border-green-300 text-green-800 line-through' 
                      : 'bg-gray-50 border-gray-200'
                  }`}
                >
                  <div className="font-semibold">{wordObj.word}</div>
                  <div className="text-sm text-gray-600">{wordObj.translation}</div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

