          ]
        },
        {
          person: "Tu",
          pronoun: "te/t'",
          examples: [
            { french: "Tu te reposes", spanish: "Te descansas", type: "reflexif" },
            { french: "Tu t'amuses", spanish: "Te diviertes", type: "pronominal" },
            { french: "Tu te trompes", spanish: "Te equivocas", type: "pronominal pur" }
          ]
        },
        {
          person: "Il/Elle/On",
          pronoun: "se/s'",
