      agreement_rules: [
        { rule: "Concordancia obligatoria", example: "Marie est partie" },
        { rule: "Masculino plural", example: "Ils sont arrivés" },
        { rule: "Femenino plural", example: "Elles sont venues" }
      ]
    },
    { 
      verb: "avoir", 
