@layer components {
  .animated-ball {
    position: absolute;
    border-radius: 50%;
    animation: float 6s ease-in-out infinite;
  }

  .animated-ball:nth-child(1) {
    background: hsl(25, 95%, 53%);
    width: 60px;
    height: 60px;
    top: 20%;
    left: 10%;
    animation-delay: 0s;
