    width: 80px;
    height: 80px;
    top: 60%;
    right: 15%;
    animation-delay: 2s;
  }

  .animated-ball:nth-child(3) {
    background: hsl(25, 95%, 53%);
    width: 40px;
    height: 40px;
    top: 80%;
    left: 20%;
    animation-delay: 4s;
  }

  .animated-ball:nth-child(4) {
    background: hsl(45, 93%, 58%);
    width: 50px;
    height: 50px;
    top: 30%;
    right: 30%;
    animation-delay: 1s;
  }

  .animated-ball:nth-child(5) {
    background: hsl(25, 95%, 53%);
    width: 70px;
    height: 70px;
    top: 10%;
    right: 10%;
    animation-delay: 3s;
  }

  .animated-ball:nth-child(6) {
    background: hsl(45, 93%, 58%);
    width: 45px;
    height: 45px;
    top: 70%;
    left: 50%;
    animation-delay: 5s;
  }
}

@keyframes float {
  0%, 100% {
    transform: translateY(0px) translateX(0px);
    opacity: 0.7;
  }
  50% {
    transform: translateY(-30px) translateX(20px);
    opacity: 0.9;
  }
}

.gradient-text {
  background: linear-gradient(45deg, var(--netflix-red), var(--russian-purple), var(--french-blue));
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

