            <CardTitle className="text-xl text-white">Notas Culturales</CardTitle>
          </CardHeader>
          <CardContent className="space-y-3">
            {alphabet.cultural_notes.map((note: any, index: number) => (
              <div key={index} className="bg-gray-800/50 rounded-lg p-4">
