          {Array.from({ length: 20 }).map((_, i) => (
            <div
              key={i}
              className="absolute animate-bounce rounded-full"
              style={{
                width: Math.random() * 60 + 20 + 'px',
                height: Math.random() * 60 + 20 + 'px',
                left: Math.random() * 100 + '%',
                top: Math.random() * 100 + '%',
                background: Math.random() > 0.5 
                  ? 'linear-gradient(45deg, #ff6b35, #f7931e)' 
                  : 'linear-gradient(45deg, #ffd23f, #ff6b35)',
                opacity: 0.7,
                animationDelay: Math.random() * 3 + 's',
                animationDuration: (Math.random() * 3 + 2) + 's'
              }}
            />
          ))}
        </div>
      </div>

      <Card className="w-full max-w-md bg-gray-900/90 border-gray-700 relative z-10">
        <CardHeader className="text-center">
          <CardTitle className="text-2xl font-bold text-white">√önete a CandyWeb</CardTitle>
          <CardDescription className="text-gray-300">
            Crea tu cuenta para acceder a todos nuestros juegos de idiomas
          </CardDescription>
        </CardHeader>
        <CardContent>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <Input
                type="text"
                placeholder="Nombre de usuario"
                value={formData.username}
                onChange={(e) => setFormData({ ...formData, username: e.target.value })}
                className="bg-gray-800 border-gray-600 text-white"
                required
              />
            </div>

            <div>
              <Input
                type="email"
                placeholder="Correo electr√≥nico"
                value={formData.email}
                onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                className="bg-gray-800 border-gray-600 text-white"
                required
              />
            </div>

            <div>
              <Input
                type="password"
                placeholder="Contrase√±a"
                value={formData.password}
                onChange={(e) => setFormData({ ...formData, password: e.target.value })}
                className="bg-gray-800 border-gray-600 text-white"
                required
              />
            </div>

            <div>
              <Input
                type="password"
                placeholder="Confirmar contrase√±a"
                value={formData.confirmPassword}
                onChange={(e) => setFormData({ ...formData, confirmPassword: e.target.value })}
                className="bg-gray-800 border-gray-600 text-white"
                required
              />
            </div>

            <Button 
              type="submit" 
              className="w-full bg-gradient-to-r from-orange-500 to-red-500 hover:from-orange-600 hover:to-red-600"
              disabled={registerMutation.isPending}
