export default function TreasureHuntGame({ language, onComplete }: TreasureHuntGameProps) {
  const [currentStep, setCurrentStep] = useState(0);
  const [selectedOption, setSelectedOption] = useState<number | null>(null);
  const [showResult, setShowResult] = useState(false);
  const [score, setScore] = useState(0);
  const [attempts, setAttempts] = useState(5);
  const [timeLeft, setTimeLeft] = useState(120); // 2 minutos
  const [gameStarted, setGameStarted] = useState(false);
  const [gameEnded, setGameEnded] = useState(false);
  const [isGameActive, setIsGameActive] = useState(false);
  const [mapPosition, setMapPosition] = useState({ x: 50, y: 80 });

  const directions = treasureData[language as keyof typeof treasureData];
  const currentDirection = directions[currentStep];

  // Temporizador
  useEffect(() => {
    if (!gameStarted || gameEnded || timeLeft <= 0) return;

    const timer = setInterval(() => {
      setTimeLeft(prev => {
        if (prev <= 1) {
          setGameEnded(true);
          setIsGameActive(false);
          onComplete(false);
          return 0;
        }
        return prev - 1;
      });
    }, 1000);

    return () => clearInterval(timer);
  }, [gameStarted, gameEnded, timeLeft, onComplete]);

  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  const handleOptionClick = (optionIndex: number) => {
    if (showResult || gameEnded) return;
    setSelectedOption(optionIndex);
  };

  const confirmDirection = () => {
    if (selectedOption === null || gameEnded) return;

    setShowResult(true);
    const isCorrect = selectedOption === currentDirection.correct;

    if (isCorrect) {
      // Add points for correct direction
      const pointsEarned = 150 + (timeLeft * 3); // Base points + time bonus
      setScore(prev => prev + pointsEarned);

      // Update global score tracker
      if (window.updateGameScore) {
        window.updateGameScore(pointsEarned);
      }

      // Mover en el mapa
      const newPosition = { ...mapPosition };
      if (currentStep === 0) newPosition.y -= 15; // norte
      else if (currentStep === 1) newPosition.x += 15; // derecha
      else if (currentStep === 2) newPosition.y -= 10; // continuar

      setMapPosition(newPosition);

      setTimeout(() => {
        if (currentStep < directions.length - 1) {
          setCurrentStep(prev => prev + 1);
          setSelectedOption(null);
          setShowResult(false);
        } else {
          setGameEnded(true);
          setIsGameActive(false);
          onComplete(true);
        }
      }, 2000);
    } else {
      setAttempts(prev => {
        const newAttempts = prev - 1;
        if (newAttempts <= 0) {
          setTimeout(() => {
            setGameEnded(true);
            setIsGameActive(false);
            onComplete(false);
          }, 2000);
        } else {
          setTimeout(() => {
            setSelectedOption(null);
            setShowResult(false);
          }, 2000);
        }
        return newAttempts;
      });
    }
  };

  const startGame = () => {
    setGameStarted(true);
  };

  // Pantalla inicial
  if (!gameStarted) {
    return (
      <div className="min-h-screen bg-gradient-to-b from-amber-400 to-orange-600 flex items-center justify-center">
        <div className="bg-white/90 backdrop-blur-sm rounded-2xl p-8 text-center max-w-md shadow-2xl">
          <div className="text-6xl mb-6">üó∫Ô∏è</div>
          <h2 className="text-2xl font-bold mb-4 text-gray-800">B√∫squeda del Tesoro</h2>
          <p className="text-gray-600 mb-6">
            Tienes 2 minutos y 5 intentos para seguir las {directions.length} direcciones correctamente.
            ¬°Encuentra el tesoro siguiendo las pistas en el mapa!
          </p>
          <Button 
            onClick={startGame} 
            className="bg-amber-600 hover:bg-amber-700 text-white px-8 py-3 text-lg"
          >
            ¬°Comenzar B√∫squeda!
          </Button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-b from-amber-400 to-orange-600 relative overflow-hidden">
      {/* HUD */}
      <div className="absolute top-4 left-4 right-4 flex justify-between items-center z-20">
        <div className="bg-white/90 rounded-lg px-4 py-2 flex items-center gap-2">
          <Clock className="w-5 h-5 text-blue-600" />
          <span className={`font-bold ${timeLeft <= 30 ? 'text-red-600 animate-pulse' : 'text-gray-800'}`}>
            {formatTime(timeLeft)}
          </span>
        </div>

        <div className="bg-white/90 rounded-lg px-4 py-2 flex items-center gap-2">
          <Map className="w-5 h-5 text-amber-600" />
          <span className="text-gray-800 font-semibold">Paso {currentStep + 1}/{directions.length}</span>
        </div>

        <div className="bg-white/90 rounded-lg px-4 py-2 flex items-center gap-2">
          <span className="text-orange-600 font-bold">üèÜ {score}</span>
        </div>

        <div className="bg-white/90 rounded-lg px-4 py-2 flex items-center gap-2">
          <Heart className="w-5 h-5 text-red-500" />
          <span className="font-bold text-gray-800">{attempts}</span>
        </div>
      </div>

      {/* Mapa del tesoro */}
      <div className="absolute top-20 left-1/2 transform -translate-x-1/2 w-80 h-60 bg-yellow-200 rounded-lg border-4 border-amber-800 shadow-lg">
        {/* Elementos del mapa */}
        <div className="absolute top-4 left-4 text-2xl">üèîÔ∏è</div>
        <div className="absolute top-4 right-4 text-2xl">üå≥</div>
        <div className="absolute bottom-4 left-4 text-2xl">üèûÔ∏è</div>
        <div className="absolute bottom-4 right-4 text-2xl">üï≥Ô∏è</div>

        {/* Tesoro */}
        {currentStep === directions.length - 1 && showResult && selectedOption === currentDirection.correct && (
          <div className="absolute top-1/2 right-1/4 text-3xl animate-bounce">üí∞</div>
        )}

        {/* Posici√≥n del jugador */}
        <div 
          className="absolute w-6 h-6 bg-red-500 rounded-full border-2 border-white transition-all duration-1000"
          style={{
            left: `${mapPosition.x}%`,
            top: `${mapPosition.y}%`,
            transform: 'translate(-50%, -50%)'
          }}
        >
          <div className="absolute inset-0 flex items-center justify-center text-white text-xs font-bold">
            üë§
          </div>
        </div>
      </div>

      {/* Instrucci√≥n actual */}
      <div className="absolute top-96 left-1/2 transform -translate-x-1/2 bg-white/90 rounded-lg p-6 text-center max-w-md">
        <div className="text-3xl mb-4">{currentDirection.icon}</div>
        <h3 className="text-lg font-bold mb-4 text-gray-800">Direcci√≥n:</h3>
        <p className="text-xl mb-4">{currentDirection.instruction}</p>

        {/* Opciones */}
        <div className="grid grid-cols-1 gap-3">
          {currentDirection.options.map((option, index) => (
            <button
              key={index}
              onClick={() => handleOptionClick(index)}
              className={`px-4 py-3 rounded-lg border-2 transition-all ${
                selectedOption === index
                  ? 'border-amber-500 bg-amber-100 text-amber-800'
                  : 'border-gray-300 bg-white hover:border-amber-300'
              }`}
              disabled={showResult || gameEnded}
            >
              {option}
            </button>
          ))}
        </div>
      </div>

      {/* Bot√≥n confirmar */}
      <div className="absolute bottom-20 left-1/2 transform -translate-x-1/2">
        <Button
          onClick={confirmDirection}
          disabled={selectedOption === null || showResult || gameEnded}
          className="bg-amber-600 hover:bg-amber-700 text-white font-bold px-8 py-3 text-lg shadow-lg disabled:opacity-50"
        >
          ¬°Seguir Direcci√≥n! üó∫Ô∏è
        </Button>
      </div>

      {/* Mensaje de resultado */}
      {showResult && (
        <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 bg-white/95 rounded-xl p-6 text-center shadow-2xl z-30">
          <div className="text-4xl mb-2">
            {selectedOption === currentDirection.correct ? '‚úÖ' : '‚ùå'}
          </div>
          <h3 className="text-xl font-bold mb-2 text-gray-800">
            {selectedOption === currentDirection.correct ? '¬°Correcto!' : '¬°Incorrecto!'}
          </h3>
          <p className="text-gray-600">
            {selectedOption === currentDirection.correct 
              ? currentStep === directions.length - 1 ? '¬°Has encontrado el tesoro!' : 'Contin√∫a siguiendo el mapa...'
              : attempts > 1 ? 'Int√©ntalo de nuevo...' : '¬°Se acabaron los intentos!'
            }
          </p>
        </div>
      )}

      {/* Game Score Tracker */}
      {gameStarted && (
        <div className="absolute top-4 left-1/2 transform -translate-x-1/2 z-30">
          <GameScoreTracker
            gameId="treasure-hunt"
            gameName="B√∫squeda del Tesoro"
            difficulty="Avanzado"
            language={language}
            isGameActive={isGameActive}
            onGameStart={() => {
              setIsGameActive(true);
              setScore(0);
            }}
            onGameEnd={(finalScore, completionTime) => {
              console.log(`Treasure hunt completed with score: ${finalScore}, time: ${completionTime}s`);
            }}
          />
        </div>
      )}
    </div>
  );
}

src/components/games/VacationRoutineGame.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { gameData } from "@/data/games";

interface VacationRoutineGameProps {
  language: string;
  onComplete: (success: boolean) => void;
}

export default function VacationRoutineGame({ language, onComplete }: VacationRoutineGameProps) {
  const [currentTask, setCurrentTask] = useState(0);
  const [selectedAnswers, setSelectedAnswers] = useState<number[]>([]);
  const [showThankYou, setShowThankYou] = useState(false);

  const gameInfo = gameData.vacation[language as keyof typeof gameData.vacation];

  if (!gameInfo) {
    // Datos por defecto si el idioma no est√° configurado
    const defaultGame = {
      routine: "Hello! Help me finish my vacation routine!",
      tasks: [
        { text: "I _____ at 9am", options: ["wake up", "sleep", "eat"], correct: 0 },
        { text: "I brush my _____", options: ["hair", "teeth", "hands"], correct: 1 },
        { text: "I _____ breakfast", options: ["dinner", "lunch", "have"], correct: 2 }
      ],
      thanks: "Thank you so much! Now I can enjoy my vacation!"
    };

    const handleAnswer = (answerIndex: number) => {
      const newAnswers = [...selectedAnswers, answerIndex];
      setSelectedAnswers(newAnswers);

      if (currentTask < defaultGame.tasks.length - 1) {
        setTimeout(() => setCurrentTask(currentTask + 1), 1000);
      } else {
        setTimeout(() => {
          setShowThankYou(true);
          const allCorrect = newAnswers.every((answer, index) => answer === defaultGame.tasks[index].correct);
          setTimeout(() => onComplete(allCorrect), 2000);
        }, 1000);
      }
    };

    return (
      <div className="min-h-screen bg-gradient-to-b from-blue-300 to-blue-500 flex items-center justify-center p-6">
        <div className="bg-white rounded-2xl p-8 max-w-2xl w-full text-center shadow-2xl">
          {!showThankYou ? (
            <>
              <div className="text-6xl mb-4">üë¶</div>
              <h2 className="text-2xl font-bold mb-4 text-blue-800">{defaultGame.routine}</h2>

              <div className="bg-gray-100 rounded-lg p-6 mb-6">
                <div className="text-lg mb-4 text-gray-800">
                  {defaultGame.tasks[currentTask].text}
                </div>

                <div className="grid grid-cols-1 gap-3">
                  {defaultGame.tasks[currentTask].options.map((option, index) => (
                    <Button
                      key={index}
                      onClick={() => handleAnswer(index)}
                      className="bg-blue-500 hover:bg-blue-600 text-white py-3 text-lg"
                    >
                      {option}
                    </Button>
                  ))}
                </div>
              </div>

              <div className="text-sm text-gray-600">
                Tarea {currentTask + 1} de {defaultGame.tasks.length}
              </div>
            </>
          ) : (
            <div className="text-center">
              <div className="text-6xl mb-4">üòä</div>
              <h2 className="text-2xl font-bold mb-4 text-green-600">{defaultGame.thanks}</h2>
              <div className="text-4xl">üéâ</div>
            </div>
          )}
        </div>
      </div>
    );
  }

  const handleAnswer = (answerIndex: number) => {
    const newAnswers = [...selectedAnswers, answerIndex];
    setSelectedAnswers(newAnswers);

    if (currentTask < gameInfo.tasks.length - 1) {
      setTimeout(() => setCurrentTask(currentTask + 1), 1000);
    } else {
      setTimeout(() => {
        setShowThankYou(true);
        const allCorrect = newAnswers.every((answer, index) => answer === gameInfo.tasks[index].correct);
        setTimeout(() => onComplete(allCorrect), 2000);
      }, 1000);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-300 to-blue-500 flex items-center justify-center p-6">
      <div className="bg-white rounded-2xl p-8 max-w-2xl w-full text-center shadow-2xl">
        {!showThankYou ? (
          <>
            <div className="text-6xl mb-4">üë¶</div>
            <h2 className="text-2xl font-bold mb-4 text-blue-800">{gameInfo.routine}</h2>

            <div className="bg-gray-100 rounded-lg p-6 mb-6">
              <div className="text-lg mb-4 text-gray-800">
                {gameInfo.tasks[currentTask].text}
              </div>

              <div className="grid grid-cols-1 gap-3">
                {gameInfo.tasks[currentTask].options.map((option, index) => (
                  <Button
                    key={index}
                    onClick={() => handleAnswer(index)}
                    className="bg-blue-500 hover:bg-blue-600 text-white py-3 text-lg"
                  >
                    {option}
                  </Button>
                ))}
              </div>
            </div>

            <div className="text-sm text-gray-600">
              Tarea {currentTask + 1} de {gameInfo.tasks.length}
            </div>
          </>
        ) : (
          <div className="text-center">
            <div className="text-6xl mb-4">üòä</div>
            <h2 className="text-2xl font-bold mb-4 text-green-600">{gameInfo.thanks}</h2>
            <div className="text-4xl">üéâ</div>
          </div>
        )}
      </div>
    </div>
  );
}

